INSERT INTO `books` (`id`, `name`, `author`, `description`, `status`, `price`, `image`, `categoryid`, `featured`) VALUES
(1, 'Черчилль. Біографія', 'Гілберт М.', 'У новому детективному романі сучасний швейцарський письменник, лауреат Гран-прі Французької академії та Гонкурівської премії ліцеїстів Жоель Діккер знову інтригує читачів розкриттям заплутаного злочину. Сорокап\'ятирічний капітан поліції Джесс Розенберґ, унікальний слідчий, що розкрив усі справи, за які брався, й дістав за це прізвисько Капітан 100%, вирішує піти у відставку. На прощальній церемонії, під час якої його вшановують десятки колег, з\'являється журналістка Стефані Мейлер і заявляє, що двадцять років тому в найпершій справі, яка стала визначальною в кар\'єрі Розенберґа й кардинально змінила його життя, капітан припустився фатальної помилки, через яку в убивстві чотирьох людей обвинуватили невинну особу. За кілька годин Стефані Мейлер, яка самостійно провадить розслідування того давнього злочину, таємничо зникає. Капітан Розенберґ вирішує зачекати з відставкою й береться за повторне слідство..', 1, 692, 'Черчилль.jpeg', 12, 1),
(2, 'Кладовище домашніх тварин: роман', 'Стівен Кінг', 'Усе почалося з того, що улюбленця родини Луїса Крида, кота Черча, збила вантажівка. Сусід запропонував йому поховати тварину на старому індіанському кладовищі. За легендами, воно має таємничу силу, яка здатна повертати до життя. І Луїс погодився... Уранці кіт повернувся додому живим. Майже живим...', 1, 114, 'Кладовище_дом_тварин.jpeg', 12, 1),
(3, 'Характерник: роман', 'Шкляр В. І.', 'У другій половині XVII століття в Запорозькій Січі сталася неймовірна подія, яку зафіксовано в архівах тодішньої Московії і яку не оминув увагою жоден доскіпливий хроніст козаччини. Та особливого чару ця пригода набуває під пером письменника: він на основі документальних джерел творить яскраву історичну реальність із життя «лицарів списа і шаблі». Кошовий Сірко, кинувши виклик самому цареві, розпочинає велику, вкрай небезпечну гру з Москвою. І треба зробити неможливе, аби в цьому поєдинку зберегти козацьку честь і гідність матері Січі. Хто просуне голову в пащу скаженого звіра?', 1, 118, 'Характерник.jpeg', 12, 1),
(4, 'Зникнення Стефані Мейлер', 'Діккер Ж.', 'Робота визнаного біографа Мартіна Ґілберта з представлення світові Великого Англійця посідає особливе місце серед подібних видань. В основу книги, що вперше виходить українською мовою, покладено факти, відібрані з багатьох дослідницьких джерел та ретельно проаналізовані автором, а також відомості, надані Рендольфом — сином Вінстона Черчилля. Щира й неупереджена розповідь дає змогу зрозуміти, як формувався світогляд цієї людини, як відбувалося його становлення як політика, якими були життєві цінності й пріоритети, чим йому довелося жертвувати заради торжества демократії. Читачам цікаво буде довідатися також про особисте життя та зовсім не політичні захоплення найвідомішого англійця, якого завжди впізнавали за характерним жестом пальцями у вигляді латинської букви «V», що символізує перемогу.', 1, 149, 'Зникнення_Стефані_Мейлер.jpeg', 12, 1),
(5, 'Злочини майбутнього', 'Гудмен М.', 'Марк Гудмен, один з провідних світових авторитетів у галузі глобальної безпеки, пропонує читачеві захоплюючу подорож таємними шляхами цифрового підпілля, розкриваючи способи, які використовують проти нас злочин-ці-одинаки, кримінальні корпорації, терористичні організації та цілі країни. Злочинці буквально «окупують» новітні технології, і роблять це так успішно, що ми постійно відстаємо від них зі своїми засобами захисту. Сьогодні не існує жодного комп\'ютера або смартфона, який не може бути зламаний, і нічого не можна зберегти у таємниці — від персональних даних до військових розробок, що коштують мільярди доларів. «Злочини майбутнього» — це тверезий погляд професіонала на зворотний бік технологічних інновацій і ті наслідки, якими вони загрожують нашому взаємопов\'язаному й нескінченно уразливому світу.', 1, 358, 'Злочини_майбутнього.jpeg', 1, 0),
(6, 'Айвенго', 'Скотт В.', '\"Айвенго\" — один із кращих творів славетного англійського письменника Вальтера Скотта (1771—1832). Дія роману розгортається в добу, що настала після завоювання Англії норманами, під час правління короля Річарда Левового Серця, котрому вірний товариш Айвенго допомагає боротися із непокірною знаттю. Письменник майстерно вплітає особисті долі героїв у реальні історичні події та достовірно відтворює життя тогочасного суспільства. Книга надзвичайно захоплива, повчальна і дає змогу не лише пізнати, але й відчути сувору середньовічну епоху.', 1, 322, 'Айвенго.jpeg', 1, 1),
(9, 'Джейн Ейр: роман', 'Бронте Ш.', '\"Джейн Ейр\" — безсмертний роман відомої англійської письменниці Шарлотти Бронте (1816—1855), що приніс їй неймовірну славу і став революційним у світовій літературі. Це щира розповідь головної героїні про свою долю і кохання, кожен епізод якої виписаний психологічно сильно й глибоко. Твір випереджав свій час за важливістю порушених у ньому проблем: особиста та соціальна свобода жінки, станова нерівність, питання віри тощо. Ця книга дала початок феміністичному руху в літературі, витримала безліч екранізацій і незмінно займає перші позиції у списках бестселерів.', 1, 272, 'Джейн_Ейр.jpeg', 1, 0),
(10, 'Скриня Пандори', 'Вербер Б.', 'Скільки життів проживає людина? Чи впливають попередні життя на нас теперішніх - на наші характери, вподобання, страхи? Чи готові ми в буквальному сенсі відчинити двері минулого, опинитись у вирі подій, одного разу вже пережитих? Над цим у романі «Скриня Пандори» розмірковує культовий французький письменник Бернар Вербер - як завжди, цікаво і захопливо. Дія відбувається в Парижі. Вчитель історії на уроках руйнує міфи - спростовує факти, увічнені літописцями на догоду владі і згодом переписувані наступними поколіннями істориків. Одного разу він переживає регресивний гіпноз і занурюється в минулі життя - так глибоко в минуле, що бачить світ до Великого потопу. Чи вдасться йому врятувати - якщо не саму Атлантиду, в яку мало хто вірить, то хоч би пам\'ять про неї?', 1, 248, 'Скриня_пандори.jpeg', 1, 0),
(11, 'Сяйво: роман', 'Стівен Кінг', 'Коли Торранси найнялися доглядати взимку за розкішним готелем, вони й гадки не мали, який невимовний жах чекає на них... Одного разу там скоїлася страшна трагедія: колишній доглядач зарубав сокирою власну родину. Саме тут п\'ятирічний Денні дізнався, що він може бачити справжніх мешканців будинку. І це — привиди. Хлопчику, наділеному даром передбачення, відкривається страшна суть речей. Він уже знає, звідки його родині загрожує смерть...', 1, 230, 'Сяйво.jpeg', 1, 0),
(12, 'Аптекар: роман', 'Майер С.', 'Одна з найкращих секретних агентів, вона працює на владу США, але про це ніхто не знає. Утих, хто її найняв, навіть немає назви. Єдина людина, якій вона довіряє, мертва. Незабаром така доля очікує й на неї. Агент знає про всі загрози й розуміє: треба готуватися до найжорстокішого бою, де на кону — власне життя. Але несподівана зустріч руйнує її плани...', 1, 190, 'Аптекар.jpeg', 1, 1),
(13, 'Хранителі: роман', 'Кунц. Д', 'Із надтаємної лабораторії, в якій проводилися генетичні експерименти, під час пожежі втікають два створіння, наділені майже людським розумом: пес породи ретривер і Аутсайдер — жива машина для вбивства. Перший із них стане найкращим другом Трейвіса Корнелла і Нори Дейвон, а другий -заклятим ворогом. Ситуацію ускладнює той факт, що втікачів наполегливо розшукують спецслужби і божевільний найманий убивця.', 1, 200, 'Хранителі.jpeg', 1, 0),
(14, 'Місто дівчат', 'Гілберт Е.', '«Місто дівчат» - новий бестселер Елізабет Ґілберт, яка підкорила світ романами «їсти, молитися, кохати» та «Природа всіх речей». Це інтригуючий роман про жіночу сексуальність і свободу, про відвагу бути собою і право жити так, як тобі хочеться. А ще - історія дуже незвичайного кохання, на яке наклала свою тінь війна, та справжньої дружби, яку нелегко знайти, зрозуміти та оцінити, особливо в такому непростому і мінливому місті, як Нью-Йорк. Роман «Місто дівчат» починається 1940 року, коли дев\'ятнадцятилітню Вівіан Морріс виганяють з престижного коледжу. Батьки відправляють доньку на Мангеттен до тітки - власниці невеликого, проте дуже ексцентричного театру. Несподівано для себе Вівіан опиняється у справжньому вирі довоєнного богемного життя. Знайомства із зірками, незвичайні театральні постановки, нестримний секс, ріки алкоголю і відчуття цілковитої свободи спершу заворожують та спокушають, а потім вибивають землю з-під ніг. І це лише початок шляху Вівіан довжиною в життя, який необхідно пройти, щоб зрозуміти: ти не мусиш бути хорошою дівчинкою, щоб бути хорошою людиною.', 1, 187, 'Місто_дівчат.jpeg', 1, 0),
(15, 'Чорний обеліск. Тріумфальна арка. Ніч у Лісабоні: романи', 'Ремарк Є. М.', '«Тріумфальна арка» - це роман про кохання, війну та смерть. Роман про чоловіка, який звик до страху, ненависті та безнадії і тепер бажає тільки помсти, і про жінку, яка знає, що таке успіх, але не знає, яким є справжнє кохання, про пристрасть, яка скінчилася трагедією.', 1, 180, 'Чорний_обеліск.jpeg', 1, 0),
(16, 'Вдови: роман', 'Ла Плант Л.', 'Що мають робити дружини, які втратили своїх чоловіків? Страждати, плакати, згадувати... Але Доллі, Лінда та Ширлі — не такі. Вони обирають дещо інше. Помсту. Бо їхні чоловіки не були звичайними... Коли їх убили, лишилася незавершеною їхня справа. У банківському сейфі свого чоловіка Доллі знаходить пістолет, гроші та детальні плани серії грабунків. Тих, що так і не встигли здійснити чоловіки. Доллі разом з Ліндою та Ширлі має завершити справу своїх коханих. Та щоб протистояти місцевим головорізам і поліції, удовам потрібна четверта спільниця...', 1, 175, 'Вдови.jpeg', 1, 0),
(17, 'Божественна комедія: поема', 'Данте Аліг\'єрі', 'Серед пам\'яток світової літератури геніальна «Божественна комедія» Данте Аліг\'єрі (1265—1321) посідає одне з чільних місць. У своєму найграндіознішому витворі в символічно-алегоричній формі поет зобразив драматичну долю людської душі: її «загибель» у пеклі, «переродження» в чистилищі, тріумф у раю. Поема Данте є найзнач-нішим явищем Високого середньовіччя і певною мірою передвістям культури Відродження.', 1, 172, 'Божествена_комедія.jpeg', 1, 0),
(18, 'Хірург: роман', 'Геррітсен Т.', 'Убивця вдирається в домівки самотніх жінок. Він не просто їх вбиває. Він виробляє з ними таке, через що здобув прізвисько Хірург... Душогуб ретельно відтворює стиль серійного маніяка, вбитого два роки тому. А головною його ціллю стає остання жертва попередника, якій дивом удалося вціліти. Життя Кетрін Корделл знову під загрозою...', 1, 155, 'Хірург.jpeg', 1, 0),
(19, 'Стрелок: из цикла \"Темная Башня\": роман', 'Стівен Кінг', 'Юный Роланд - последний благородный рыцарь в мире, \"сдвинувшемся с места\". Ему во что бы то ни стало нужно найти Темную Башню - средоточие Силы, краеугольный камень мироздания. Когда-нибудь он отыщет эту башню, а пока ему предстоит долгий и опасный путь - путь по миру, которым правит черная магия, по миру, из которого порой открываются двери в нашу реальность...', 1, 80, 'Темна_башня.jpeg', 2, 0),
(20, 'Під куполом', 'Стівен Кінг', 'Напередодні Гелловіна місто Честер Мілл у штаті Мейн невідома сила накриває прозорим Куполом. Навіть літаки розбиваються об цю енергетичну оболонку. Опинившись у замкненому просторі, хтось виявляє здатність до огидних злочинів, а хтось, на протидію цим покидькам, — до подвигів. Одному політикові навіть припала до душі ізольованість населення, яким тепер легко керувати. Тільки-от недовго триватиме ера його правління, адже сам Купол вороже налаштований щодо своїх заручників. Тож їхній час не просто обмежено. Він швидко спливає...', 1, 163, 'Під_куполом.jpeg', 2, 0),
(21, '451 за Фаренгейтом : повість', 'Бредбері Рей', 'Роман-антиутопія в жанрі наукової фантастики. Вперше побачив світ 1953 року. Розповідає про споживацьке суспільство майбутнього, у якому панує майже цілковитий контроль над людьми. Державні органи намагаються підпорядкувати собі навіть думки людей. Книги заборонено, незаконним вважають читання і переховування будь-якої літератури, за порядком мають слідкувати пожежники. 451? за Фаренгейтом (233? за Цельсієм) — температура, при якій загоряється папір. Пожежники не гасять вогонь, вони його розпалюють, палять книжки. Тому що пожежники — опора режиму, запорука того, що люди не читатимуть книжок, адже книги спонукають думати і аналізувати. Проте у цьому пекельному царстві темряви залишилися невеличкі духовні острівці, що зберігають рештки інтелектуального надбання людства. Це люди-книги, люди, що вивчають напам’ять зміст друкованих видань і тримають їх у своїй свідомості. Піднімаючи у «451° за Фаренгейтом» тему глобального безкультур’я та тотального відходу від дійсності суспільства, Бредбері намагається донести до читачів важливість зберігати тисячолітні культурні набутки, охороняти віковічні пласти мудрості, що були і є запорукою цивілізації. Перекладено українською мовою Євгеном Крижевичем.', 1, 90, '451.jpeg', 2, 0),
(22, 'Воно: роман', 'Стівен Кінг', 'Воно — це пекельна істота, що наче випірнула з нічних кошмарів. Воно живиться людським страхом... Колись давно семеро підлітків змогли Його пере могти. Але майже тридцять років по тому Воно повертається, щоб помститися І вже дорослі друзі мають вступити в нову битву зі Злом... Не читайте цю книг; в темряві або на самоті. Адже Воно може бути поруч.', 1, 208, 'Воно.jpeg', 2, 0),
(23, 'Артеміда : Роман', 'Вейр Е.', 'Другий науково-фантастичний роман Енді Вейра. Події роману відбуваються у 2080-х роках XXI століття на Артеміді, першому та поки єдиному місті на Місяці. Сюжет обертається навколо того, як місцева дівчина, Жасмін «Джаз» Башара, вплутується у змову за встановлення контролю над містом.', 1, 191, 'Артеміда.jpeg', 2, 0),
(24, 'Видозмінений вуглець: Роман', 'Морган Р.', 'Колишнього посланця ООН Такеші Ковача вже вбивали, але остання його смерть виявилася особливо болісною. Доправлений за сто вісімдесят світлових років від домівки й зачохлений у нове тіло в Бей-Сіті (колишньому Сан-Франциско, де вже заіржавів і занепав міст Золоті Ворота), Ковач несподівано опиняється в темному серці таємничої масштабної змови, жахливої навіть за мірками суспільства, в якому «існування» можна купувати і продавати.\r\n\r\n', 1, 180, 'Видозмінений.jpeg', 2, 0),
(25, 'Мітка : Роман', 'Рот В.', 'Автор всесвітньовідомого бестселера \'\'Дивергент\'\' Вероніки Рот представляє захоплюючий фантастичний роман, що ілюструє надзвичайну дружбу, епічну історію кохання та приголомшливі галактичні пригоди. # 1 New York Times бестселер * Wall Speet Journal бестселер * USA Today бестселер * # 1 IndieBound бестселер. У галактиці, яка живитися магічною течією - Енергією, дар є у кожного. Акос, син оракула з народу тувеситів, завдяки своєму дару має надійний захист: він здатен протистояти впливу інших людей, хай який у них буде дар. Одного нещасливого дня Акоса разом з братом викрадають солдати з ворожого народу шотетів. Опинившись у чужому краю, Акос ладен на все, щоб урятувати брата. Кіра, сестра брутального тирана, який править народом шотетів, має дар завдавати болю одним дотиком. Це дає їй владу над людьми, та водночас Кіра змушена все життя жити з болем, до того ж їй зовсім не хочеться бути клинком у руках свого старшого брата. Якщо вона допоможе Акосу, який здатен звільнити її від болю, може, це змінить цілий світ? \'\'Ми підемо за Потоком Енергії по краю галактики, допоки відтінок потоку не дійде до найтемнішого синього кольору, а потім опустимося на поверхню планети для Збирання - це друга частина традиції…\'\'', 1, 169, 'Мітка.jpeg', 2, 0),
(26, 'Шукачі безсмерття', 'Бенджамін Х.', 'У Нью-Йорк прибуває таємнича жінка, яка може передбачити майбутнє. Чутки про це досягають сім\'ї Ґолдів, і вони вирішують дізнатися, що на них чекає. Пророцтво стосується наступних п\'яти десятиліть. Саймон іде на Західне узбережжя шукати любові в Сан-Франциско. Мрійниця Клара стає ілюзіоністкою в Лас-Вегасі. Деніел, що пережив напад на Всесвітній торговий центр, стає військовим лікарем. Найстарша дитина в сім\'ї, Варя, присвячує себе дослідженню довголіття. Чи справді їхні дні злічені й від кого це залежить? Шукачам безсмертя доводиться обирати між реальністю та ілюзією, минулим і майбутнім, любов\'ю і стражданням...', 1, 165, 'Шукачі_безсмерття.jpeg', 2, 0),
(27, 'Адвокат диявола: роман', 'Нейдерман Е.', 'Я пропоную тобі те, про що ти так давно мріяв. Те, що личить твоїм амбіціям. Посада у престижній адвокатській конторі, солідна зарплатня, елітні апартаменти в Нью-Йорку та величезні можливості. Що я хочу натомість? Виконуй свою роботу. Добре виконуй. І не став запитань. Виправдовуй тих, кого виправдати неможливо: убивць, ґвалтівників, покидьків, маніяків, психів та збоченців. Відпускай гріхи найзапеклішим грішникам. Порушуй закони моралі та сумління. Виправдовуй винних. Вважаєш, що я зло в людській подобі? Можливо. Але ти — мій співучасник. І всі брудні справи робляться твоїми руками. Бо відтепер ти — адвокат диявола...', 1, 165, 'Адвокат_диявола.jpeg', 2, 0),
(28, 'Відьмак. Книга 1. Останнє бажання: роман', 'Сапковский А.', 'Біловолосий відьмак Ґеральт із Рівії, один з небагатьох представників колись численного цеху захисників людської раси від породжень нелюдського зла, мандрує невеликими королівствами, які можна охопити поглядом з вежі замку, та великими містами, отримуючи платню за те, чого навчений, - знищення віїв і з\'ядарок, стриґта віпперів. Але є у відьмака і власний кодекс, у якому вбивство - це лише крайня міра, а життя розумне, чим би воно не було, - це все-таки життя. Саме цим він наживає собі нових ворогів, але й знаходить друзів, які колись змінять його долю.', 1, 115, 'Відьмак.jpeg', 2, 0),
(29, 'Ліки від смерті: Роман (Той, що біжить лабіринтон) Кн. 3', 'Дешнер Дж.', 'Це кінець. «БЕЗУМ» відібрав у Томасавсе: життя, спогади, друзів-глейдерів. Але ж усе нарешті позаду! Випробування майже закінчилися — лишилося одне-єдине, останнє. Та чи переживе його бодай хтось? Однак «БЕЗУМ» не знає, що Томас пригадав значно більше, ніж можна припустити, і тому не вірить жодному слову цієї організації Нехай правда виявиться жахливою, але Томас здолав Лабіринт, здолаї випробування вогнем, і він готовий ризикнути всім, щоб урятуватв друзів. Прийшов час покласти край брехням і облуді.\r\n\r\n', 1, 103, 'Ліки від смерті.jpeg', 2, 0),
(30, 'Нічна: роман-фентезі', 'Нойвілль А.', 'Нічні — вкрай небезпечні для людей. Орден ловців винищував їх роками. Юна нічна — марра Ейпа, яка змушена приховувати своє єство, укладає ризиковану угоду з ловцем Елвердом. У неї був вибір: довіритися йому або втекти світ за очі, загинути за брата або зрадити його. І вперше Ейна наважується дослухатися до голосу серця.', 1, 99, 'Нічна.jpeg', 2, 0),
(31, 'Коефіцієнт поцілунку ', 'Хоанг Г.', 'Стелла завжди вірила в… математику. Ця наука могла логічно пояснити все, що відбувається довкола. Алгоритми, розрахунки — завдяки їм можна передбачити будьякі дрібниці. Наприклад, поведінку покупців та їхню реакцію на новий продукт. Цими «передбаченнями» Стелла й заробляла собі на життя. Успішна на роботі, дівчина нещасна в особистому житті через синдром Асперґера їй важко заводити знайомства й налагоджувати стосунки з чоловіками. Одного дня тридцятирічна самотня Стелла вирішує, що настав час опанувати науку любові. Її вчителем стає Майкл — красунчик зі служби ескортпослуг.\r\n\r\nЙому відомо все про найпотаємніші бажання жінок. Чиста логіка й ніяких почуттів. Та кохання існує поза будьякою логікою. І розв’язати це рівняння головним героям доведеться удвох…', 1, 209, 'Коефіцієнт.jpeg', 3, 0),
(32, 'Фіалки в березні: роман', 'Джіо С.', 'Успішна молода письменниця Емілі Вілсон вирушає з Нью-Йорка на острів Бейн-бридж, щоб навідати двоюрідну бабусю Бі. Дівчина пережила розлучення та творчу кризу, тож сподівається, що за межами мегаполісу зуміє опанувати свої думки й почуття. Однак життя на березі океану вже не таке безтурботне, яким здавалося в дитинстві під час канікул. Деякі мешканці острова неначе перебувають у мовчазній змові. Емілі захоплюється новим знайомим, Джеком, але бабуся Бі всіляко виказує щодо цього невдоволення. До того ж дівчина знаходить дивний щоденник, який 1943 року належав якійсь Естер Джонсон. Поринувши в читання, Емілі вирішує, що мусить розібратися з таємницями минулого, своїми й чужими.', 1, 165, 'Фіалки.jpeg', 3, 0),
(33, 'Любов дика та прекрасна: роман', 'Смолл Б.', 'Шотландія, 1577 рік. Катріона — невимовно приваблива дівчина, в яку закохується кожен, хто бодай раз бачив її вроду. Проте краса стає для дівчини прокляттям. Коли Кат виповнюється п’ятнадцять, вона змушена взяти шлюб із графом Патріком Леслі, який їй нелюбий. Однак граф добрий до Катріони. Роки злагідного подружнього життя руйнує шотландський король Джеймс Шостий. Він силою бере Катріону за свою коханку. Звинувачена в зраді та принижена Катріона тікає до Френсіса — брата-бастарда короля. Він захищає її та по-справжньому розуміє. Чи зможуть закохані подолати всі перепони і нарешті поєднати свої серця в танку дикої та прекрасної любові?', 1, 145, 'Любов_дика.jpeg', 3, 0),
(34, 'Зірка для тебе. Роман', 'Корній Д.', 'Додому до Майї та Філіпа листоноша приносить пакунок. І в цьому нібито й немає нічого дивного, але ж він адресований... Жахаськові! Хто це? І що в пакунку? Виявляється, що це прибулець, який вирішив провести канікули на якійсь дикій, примітивній планеті... На Майю, Філіпа та їхніх батьків чекає багато несподіванок. І до візиту прибульця вони зовсім не готові! Немає в них ані солоних забарчиків, і ані притиканців до гри в босоручки...', 1, 125, 'Зірка.jpeg', 3, 0),
(35, 'Секрет', 'Кетрін Г\'юз', 'Мері має секрет. Сорок років тому вона наважилася на вчинок, що назавжди змінив її життя - і невідворотно вплинув на життя дуже дорогої для неї людини. Бет шукає відповідь. Вона так і не дізналася правду про своїх батьків, але тепер мусить - інакше її дитина помре. І от, знайшовши потьмянілу газетну вирізку серед материних речей, вона розуміє, що забезпечити майбутнє її синові можна лише занурившись у минуле. Вона мусить повернутися до самого початку... і розгадати Секрет.', 1, 115, 'Секрет.jpeg', 3, 0),
(36, 'Провина зірок: Роман', 'Грін Дж.', 'Що цікавого чекає шістнадцятирічну Гейзел Грейс на не надто веселих зібраннях групи підтримки для хворих на рак дітей? Та виявляється, саме там можна зустріти кохання свого життя — кумедного і винахідливого Огастаса Вотерса, який, наче справжній джин, здатен виконати твоє найзаповітніше бажання... Сповнений гіркої радості роман Джона Гріна про життя, смерть і справжню любов завоював серця читачів у всьому світі.', 1, 120, 'Половина.jpeg', 3, 0),
(37, 'Ритуал (Фентезі)', 'Дяченко М. та С.', 'Перше кохання і перша зрада. Як зрозуміти, де добро, а де зло, якщо добро викликає жах, а зло приваблює красою? \"Ритуал\" є  одним з найперших, найсильніших і найпопулярніших творів знаменого подружжя Марини та Сергія Дяченків, визнаних найкращими фантастами Європи.\r\n\r\n', 1, 98, 'Ритуал.jpeg', 3, 0),
(38, 'Записник. Сторінки нашого кохання', 'Спаркс Н.', 'Роман про Кохання, яке тривало з першого погляду до останньго зітхання. Він і Вона походили з різних прошарків суспільства, але ні воля батьків, ні Друга світова, ні перспективний наречений не завадили їхньому коханню. Екранізований бестселер.', 1, 96, 'Записник.jpeg', 3, 0),
(39, 'П’ятдесят відтінків. Книга перша: Роман', 'Джеймс Е. Л.', 'Звичний світ студентки Анастасії Стіл перевернувся з ніг на голову того дня, коли вона вирушила брати інтерв\'ю в мільярдера Кристіяна Грея. Багатій виявився молодим, вродливим і... дуже загадковим. Які темні бажання він приховує?.. До уваги читачів — історія жаги і еротичних фантазій, відкриттів і сумнівів, кохання і пристрасті у складних, але запаморочливих стосунках. У світі продано вже понад 125 мільйонів примірників трилогії « П\'ятдесят відтінків», перекладеної на 45 мов.', 1, 110, 'П\'ятдесят_відтінків.jpeg', 3, 0),
(40, 'Мене звати Люсі Бартон: Роман', 'Страут Ел.', 'Після нескладної операції лікарі не квапляться виписувати Люсі Бартон, адже її організм відмовляється видужувати. Причини хвороби Люсі невідомі, однак саме через ці ускладнення до неї приїжджає мама, з котрою героїня не спілкувалася впродовж багатьох років. Це історія життя людини, котра зазнала чимало лиха в дитинстві, та не зламалася.', 1, 65, 'Люсі_Бартон.jpeg', 3, 0),
(41, 'Ось відкрита долоня: збірка', 'Вдовиченко Г.', 'Лів отримала картину «Дівчина, яку ти покинув» у подарунок від чоловіка. А коли він трагічно загинув, цілком утратила сенс життя. Лише полотно нагадувало їй про найщасливіші миті з коханим. Здавалося, вона відчуває його подих, чує сміх... Та одна випадкова зустріч змінює її жиїтя. Лів нарешті дізнається, хто зображений на картині, дізнається історію цієї дивовижної жінки, яка незбагненним чином вплелася до її власної долі. Неймовірні пошуки, нові зустрічі й знайомства надихають Лів. Вона знову вчиться сподіватися й вірити, вона знову чекає на кохання...', 1, 58, 'Долоня.jpeg', 3, 0),
(42, 'Два кроки назустріч', 'Сімсіон Г.', 'Розгублена й пригнічена, після смерті чоловіка Зої вирушає з Лос-Анджелеса до містечка Клюні у Франції, до своєї університетської подруги, і потрапляє на Каміно-де-Сантьяго — Дорогу святого Якова, популярний паломницький маршрут. У Мартіна є чіткий план і меркантильний інтерес на Каміно, до того ж він має купу нерозв\'язаних проблем. «Дорога змінить вас», — сказав один досвідчений паломник. Ці двоє зустрінуться на ній і пройдуть свої такі різні шляхи, щоб зрештою все зрозуміти й знову навчитися любити...', 1, 68, 'Два_кроки.jpeg', 3, 0),
(43, 'Всі пригоди Шерлока Холмса', 'Конан Дойль А.', 'Пропонована книга — збірка захоплюючих оповідань класика англійської літератури Артура Конана Дойла (1859—1930) про всесвітньо відомого детектива Шерлока Холмса. Це розповідь про людину, котра бачить і чує все те саме, що й інші, але помічає значно більше і робить висновки, які відновлюють справедливість, вирішують суперечки, а іноді навіть рятують людям життя. Якою б дивною не видавалася ситуація, для Шерлока Холмса вона завжди має логічне пояснення, тому він сміливо береться за найскладніші справи й успішно вирішує їх.', 1, 790, 'Шерлок.jpeg', 4, 0),
(44, 'Блискучий розум: роман', 'Назар С.', 'Є безліч історій про злети й падіння непересічних особистостей. Але історія життя геніального математика Джона Неша — унікальна. Авторці вдалося склеїти тисячі шматочків — із сотень інтерв\'ю, листів, документів — у приголомшливу розповідь про вченого, який зробив революцію у вивченні теорії ігор. Блискуча біографія, повна моментів, знайомих кожному з нас, захоплива історія життя, яка нікого не залишить байдужим, надзвичайно зворушлива книжка, що вплинула на тисячі читачів!..', 1, 240, 'Блискучий_розум.jpeg', 4, 0),
(45, 'Мунрейкер : детектив', 'Флемінг Ієн', '«Монархія — річ тендітна, і зазвичай її розквіт чи занепад залежить від низки випадковостей. Бува, чубляться між собою потенційні королі та королеви за право сісти на трон імперії, а потім — раз — виринає нізвідкіль такий собі лікар Гільйотен або червоногвардієць з рушницею — і нема ще одного Королівського Дому. Така біда ледь не сталася з Великою Британією. Тьфу, тьфу, тьфу…. Кого б любили тоді, якби у далекому 1955-му Єлизавету ІІ один підступний лиходій перетворив на радіаційний порох? Шведського Карла №16 Густава чи монакського князя Альбера №2? Можливо. Ну точно не колумбітового короля Х’юго Дракса.\r\nСлава Богу, є герої, які хоч і з превеликими труднощами, але спроможні рятувати цивілізацію від занепаду. Про одного з таких ця книга. Про того, хто у вогні не горить, і в воді не тоне….\r\nРутинна робота та флірт з секретарками — хіба це достойне заняття для офіцера та джентльмена на ім’я Бонд-Джеймс-Бонд за номером 007? Інша річ, коли йдеться про велику гру проти шулерів у лондонському клубі (Est. хз-коли), перегони на авто та викриття шпигунської мережі. І, звісно, про рятування Британської корони! Тут Кавалер Ордена св.Михаїла та Георгія на коні.\r\n«Мунрейкер» — третій підручник за редакцією Ієна Флемінга від видавництва «Навчальна книга «Богдан»: вчимося грати у бридж (майстерно блефувати та правильно тасувати колоди), розбиратися в ракетній техніці, ганяти на «Бентлі» (прощавай улюблений кабріолет 1930 року!), спостерігати за птахами (бо вони передвісники біди) та нишпорити по кишенях. Вчимося плавати з голими дівчатами та насолоджуємось вишуканими стравами…\r\nЛедь не забув — тренуймо щелепи! Хтозна, що доведеться ними робити, коли ти зв’язаний по руках та ногах….', 1, 199, 'Мунрейкер.jpeg', 4, 0),
(46, 'Адвокат негідників: Роман', 'Грішем Дж.', 'Себастьян Руд захищає людей, до яких інші адвоката і близько не підійшли б: підлітка-наркомана, огидного кримінального авторитета, засудженого до смертної кари; домовласника, заарештованого за стрілянину по спецпризначенцях. Чому такі клієнти? Бо він вірить, що кожен має право на справедливе судочинство, навіть якщо йому доведеться трохи змахлювати. Він ненавидить несправедливість, йому не подобаються страхові компанії, банки чи великі корпорації; він не довіряє усім щаблям влади і кепкує з концепцій системи щодо етичної поведінки.', 1, 189, 'Адвокат_негідників.jpeg', 4, 0),
(47, 'Нерв', 'Раян Дж.', '\"Нерв\" - приголомшливий молодіжний трилер про актуальні проблеми сучасності та нашого з вами майбутнього. У цьому захоплюючому динамічному романі межа між віртуальним світом та реальністю дуже тонка. Бажаючи знайти гроші на навчання, Ві стає учасницею популярної онлайн-гри під назвою \"Нерв\", яка транслюється в прямому ефірі. Учасники цього реаліті-шоу мають виконувати різноманітні завдання, які придумують для них інші користувачі. У разі успіху гравці отримують приз у вигляді грошової винагороди. І чим складніші та небезпечніше завдання, тим більша нагорода. Проте ставки ростуть, і те, що почалося для Ві як весела та хвилююча забава поступово перетворюється на справжню небезпеку для життя, де наступне завдання може стати останнім... Що можна дізнатись про людину в інтернеті? І для чого може бути використана ця інформація? Ві не задумувалась над цим. А дарма… Про автора Джинні Раян - сучасна американська письменниця, яка працює в жанрі трилер, наукова фантастика та уoung аdult. Популярність до неї прийшла після публікації дебютного роману \"Нерв\", права на екранізацію якого були викуплені в 2014 році. І вже через декілька років відбулася прем’єра молодіжного трилеру з однойменною назвою. Головні ролі в кінострічці зіграли популярні актори Емма Робертс та Дейв Франко.', 1, 158, 'Нерв.jpeg', 4, 0),
(48, 'Картковий будинок: Роман (Картковий будинок) Кн1', 'Доббс М.', 'Як колишній керівник виборчого штабу Марґарет Тетчер, Майкл Доббс знав усі входи й виходи у Вестмінстерському палаці, знав усі його підводні течії і таємниці. От і не дивно, що його політичний трилер «Картковий будинок» миттєво став бестселером, за яким двічі знімали серіал — спершу в Британії, а потім у США. Головний герой роману, Френсис Уркгарт, заради кар\'єри готовий піти по головах, та чи насмілиться він переступити останню межу — зважиться він на вбивство?', 1, 150, 'Картковий_будинок.jpeg', 4, 0),
(49, 'Небезпека дому на околиці: роман', 'Крісті А.', 'Навіть на відпочинку Еркюль Пуаро опиняється в центрі дивної та небезпечної історії. Увагу знаменитого детектива привертає справа Нік Баклі — молодої власниці маєтку «Дім на околиці», якій вже тричі дивом вдалось уникнути смерті. То масивна картина несподівано зірвалася зі стіни і впала на ліжко дівчини, то гальма в її автомобілі раптово вийшли з ладу, то її ледь не розчавив величезний валун. Та це ніщо порівняно з кульовим отвором, який Пуаро знаходить на капелюшкові Нік. Куля пройшла у кількох сантиметрах §ід голови дівчини. А отже, усі ці випадки — не звичайний збіг, як гадала вона. Це чиясь моторошна жага занапастити беззахисну Нік. Та який мотив? Чи, можливо, хтось хоче викликати на особистий двобій самого Еркюля Пуаро?', 1, 120, 'Небезпека_дому.jpeg', 4, 0),
(50, 'Непрохані: Роман', 'Маршалл М.', 'Для колишнього поліцейського Джека Вейлена все починається з візиту давнього шкільного приятеля-юриста, який просить Джекової допомоги. Приятель мав справу з відомим науковцем, та нещодавно родину науковця жорстоко вбили, а сам він зник без сліду. Однак у Джека є проблеми нагальніші: в нього самого щезла дружина, поїхавши у звичайнісіньке відрядження. Але в цьому клубку випадкових невипадковостей є ще одна зникла особа: дев\'ятирічна дівчинка, яку начебто викрали. Кояться дуже дивні речі, і зрештою Джек змушений буде взятися за справу, та чи сподобаються йому таємниці, які він почне відкопувати?', 1, 91, 'Непрохані.jpeg', 4, 0),
(51, 'Зруйнована гавань', 'Френч Т.', 'У невеличкому містечку неподалік Дубліна скоєно напал на сім\'ю Спейнів — біля моря, у наїїінзбудовапому, напівзаселеному житловому масиві. Розслідувати злочин випадає двом детективам: Майклу Кеннеді, одному з найкращих у відділку вбивств, і новачку Річі Куррану. Відомо, що це розслідування має найвищий пріоритет. Справа на перший погляд проста: чоловік убив своїх дітей і намагався вбити дружину, а потім наклав на себе руки. Проте деталі — відсутність слідів злому, дірки у стінах, безліч відеокамер та мобільних пристроїв, ніби родина когось чекала, — вказують на інше...', 1, 230, 'Зруйнована_гавань.jpeg', 4, 0),
(52, 'Пасажир №23', '', 'Дія психологічного детективу «Пасажир № 23» відбувається у відкритому морі — місці, що ніби створене для досконалого злочину. Дружина та син детектива Мартіна Шварца вирушили в круїз на лайнері й зникли. П\'ять років по тому невідома жінка, пасажирка того самого судна, повідомляє, що знає дещо про його сина, який, можливо, залишився живий. Мартін вирушає в подорож як агент під прикриттям і дізнається, що на круїзних лайнерах щороку зникають двадцять три людини. І серед них — маленька дівчинка, яка щезла на тому самому лайнері й знову з\'явилася. З іграшкою його сина в руках... Для шанувальників якісних детективів із непередбачу-ваним фіналом.', 1, 175, 'Пасажир.jpeg', 4, 0),
(53, 'Дама зникає', 'Вайт Етель Ніна', 'Айріс Kapp, яскрава представниця золотої молоді, повертається додому в Англію після відпочинку на Балканах. Це чи не вперше, коли вона подорожує сама, без галасливої компанії друзів і залицяльників, яких, немов магніт, притягують її чималі статки. У потязі Айріс знайомиться із милою балакучою жіночкою на ім\'я міс Фрой. Втомлена подорожжю і безкінечними історіями супутниці, дівчина засинає, а прокинувшись, помічає, що мила пані щезла без сліду- Айріс починає пошуки зниклої, але всі навколо стверджують, що ніякої міс Фрой у вагоні не було...', 1, 120, 'Дама.jpeg', 4, 0),
(54, 'Код да Вінчі', 'Браун Д.', 'Інтрига, навколо якої розгортаються події роману, пов\'язана з одвічною загадкою людства: походження, особистість, родина Ісуса Христа. Відповіді на цю загадку активно, але з ризиком для власного життя, шукають герої книги — гарвардський учений Роберт Ленґдон та його несподівана супутниця, криптограф Софі. Дивні, часом неймовірні відкриття чекають героїв на їхньому небезпечному, ризикованому шляху до істини. Але дійти до істини чи й пощастить аж майбутнім дітям героїв, які (знов інтрига!) можуть виявитися нащадками Ісуса Христа.', 1, 140, 'Код.jpeg', 4, 0),
(55, 'Франклін Рузвельт: життя політика', 'Деллек Р.', '«І через сто років люди, стоячи на колінах, дякуватимуть Богові за те, що в Білому домі перебував Франклін Рузвельт». Ці слова з некрологу, надрукованого у квітні 1945 року газетою «Нью-Йорк тайме», яскраво свідчать про значення постаті видатного президента в американській та світовій історії. Саме завдяки Франкліну Делано Рузвельту Сполученим Штатам вдалося подолати найглибшу в історії економічну кризу 1929-1933 років, зробити визначний внесок у перемогу в Другій світовій війні, а далі запропонувати світу модель післявоєнного устрою, яка й досі визначає міжнародну політику. В цьому біографічному виданні, що вперше виходить українською мовою і ґрунтується на рідкісних архівних документах, свідченнях учасників подій та інших достовірних джерелах, описується становлення авторитетного світового лідера, якого американський народ чотири рази — безпрецедентний випадок! — обирав на посаду президента. Навіть інформаційно підкований читач знайде тут багато нового й цікавого про досягнення й перемоги Франкліна Рузвельта, про його помилки, вагання й розчарування, про взаємини в родині, стосунки з прем\'єр-міністром Великої Британії Вінстоном Черчиллем, радянським диктатором Йосипом Сталіним, лідером Китаю Чан Кайші.', 1, 653, 'Рузвельт.jpeg', 5, 0),
(56, 'Команда суперників. Біографія Лінкольна', 'Кірнс Г.', 'Якщо поглянути на постать президента США Авраама Лінкольна, може здатися, що він узявся нізвідки — юрист із глушини, котрий відслужив один термін у палаті представників і двічі підряд не зміг потрапити до сенату. Дехто каже, що його несподіване висунення у президенти — просто випадковість. Але історія показала, що успіх Лінкольна зумовлювався гострим розумом, умінням покладатися на себе, жорсткими амбіціями та неабияким ораторським хистом. Яким бачило Лінкольна його оточення? Як йому вдалося перетворити своїх суперників на потужну команду соратників? У цій книжці-біографії, заснованій на листах і щоденникових записах, авторка змальовує портрети чотирьох конкурентів, найяскравішим з яких був Лінкольн. За образом усім відомого політика у костюмі й капелюсі вона показує живу особистість, яка ніколи не втрачала віру в себе та свою країну.', 1, 635, 'Лінкольн.jpeg', 5, 0),
(57, 'Жак Адамар - легенда математики', 'Мазья В.', 'Книга посвящена описанию жизни и творчества великого французского математика Жака Адамара (1865—1963), работы которого оказали огромное влияние на развитие математики в XX веке. В первой части излагается история жизни Жака Адамара. На страницах книги воссоздана атмосфера научной и общественной жизни конца XIX — первой половины XX века. Обилие интересных исторических подробностей и широкий ряд упоминаемых исторических персонажей и событий, относящихся не только к математике, сделают эту книгу увлекательной для любого читателя. Вторая часть представляет собой обзор математических достижений Адамара. Ему принадлежит множество классических результатов в самых разных областях математики — в теории функций, вариационном исчислении, теории чисел, аналитической механике, алгебре, геометрии, теории вероятностей, теории уравнений в частных производных и т. д. Помимо материала, относящегося непосредственно к математической деятельности ученого, приводится много интересных сведений по истории математики XIX и XX веков. Книга адресована всем интересующимся историей науки.', 1, 429, 'Adamar.jpeg', 5, 0),
(58, 'Щоденники воєнного часу 1939-1945', 'Ліндгрен А.', 'Перше видання українською мовою щоденників Астрід Ліндґрен. Нотатки про сім років життя шведського подружжя середнього класу. Ліндґрен пише про сімейні свята та рішення політичних лідерів, нестачу продуктів та життя королів. Книжка містить переклад усіх газетних вирізок і листів, які були використані у першому шведському виданні.', 1, 420, '1939.jpeg', 5, 0),
(59, 'Життя Марлен Дітріх у 2х томах', 'Ріва М.', 'Спогади Марії Ріва, дочки харизматичної зірки сцени та екрана Марлен Дітріх, шокували шанувальників актриси, чию славу протягом десятиліть підтримували не тільки її ролі в кіно, але також їдка дотепність, фантастична амбітність, неймовірна енергія та ореол бісексуальної містики. Велика Марлен оживає на цих сторінках у всіх своїх втіленнях: музи, матері, дружини, коханки, еталона голлівудської зірки, творця нових уявлень про красу та жіночність. Марія Ріва написала абсолютно чесну книгу, глибоку і багатопланову, насичену унікальними подробицями голлівудського життя, нічого не приховуючи і не прикрашаючи. Саме завдяки цьому «Життя Марлен Дітріх» вважається одним із кращих життєписів, що побачили світ наприкінці ХХ століття.', 1, 408, 'Marlen.jpeg', 5, 0),
(60, 'Важкі рішення', 'Клінтор Гіларі Родем', 'Після програшу на президентських перегонах 2008 року, Гілларі Клінтон невдовзі приймає пропозицію посісти посаду держсекретаря, де пліч-о-пліч працює зі своїм колишнім суперником - Бараком Обамою. Ця книга - відверті спогади Клінтон про свою роботу у ролі очільника Держдепартаменту, який відповідає за один з основних напрямків діяльності Адміністрації президента - зовнішню політику. Книга «Важкі рішення» розповідає нам про те, що відбувалося за «зачиненими дверима»: проливає світло на війну в Афганістані та злісну проблему США - Сирію; показує, як Америка змогла вийти зі скандалу з \\VikiLeaks, не втративши обличчя; вкотре піднімає такі важливі питання, як проблема зміни клімату та порушення прав людини. У своїй книзі Держсекретар постає перед нами і як сильна жінка-політик, і як любляча донька, дружина та матір. Клінтон довелося працювати у непрості часи, які вимагали важких рішень. Саме про ці рішення вона відверто розповідає у своїх мемуарах.', 1, 355, 'Важкі_рішення.jpeg', 5, 0),
(61, 'Ейнштейн. Життя і всесвіт генія', 'Айзексон В.', 'Ім\'я Альберта Ейнштейна стало синонімом генія. Він відкрив найвідомішу формулу в історії науки і створив теорію, яка докорінно змінила наш погляд на світ і природу реальності. Йому випало жити в бурхливу епоху воєн і революцій, політичних катастроф і наукових проривів. Цю епічну біографію написано на основі широкого корпусу архівних джерел і досі не відомих листів Ейнштейна. Волтер Айзексон, сучасний класик біографічного жанру, розповідає, як мислив геній, чим він жив, у що вірив. Це історія людини, яка зберегла в собі дитячу допитливість і відчуття дива. Інтелектуальна одіссея Ейнштейна почалася, коли шестирічним хлопчиком він заворожено дивився на стрілку компаса. А закінчилася в американському містечку з літературною назвою Ітака, де він провів останні двадцять років життя, працюючи в Інституті перспективних досліджень. Ейнштейн мав рацію і помилявся, любив людей і ненавидів тиранію — немає сумнівів, що це велике ім\'я залишиться в літописі людства назавжди.', 1, 318, 'Ейнштейн.jpeg', 5, 0),
(62, 'Пікассо. живопис, що шокував світ', 'Майлз Ю.', 'Книжка американського мистецтвознавця Майлза Юнгера — це розповідь про те, як невідомий юний художник із Барселони на ім\'я Пабло Руїс став «тим самим Пікассо», найвпливовішим митцем XX століття. Його перші кроки в Парижі були позначені відчайдушною боротьбою, часто відчаєм і зневірою, але Пікассо пізніше згадував їх як найщасливіші в житті. Це історія художнього генія з особливим творчим даром, сповнена розчарувань та триумфів, відчаю, кохань та марень в атмосфері богемного Монмартра — осердя паризького авангарду, розпусти, анархізму та злочинності. Саме там за драматичних обставин народився один із великих шедеврів, що докорінно змінили шляхи сучасного мистецтва.', 1, 256, 'Пікассо.jpeg', 5, 0),
(63, 'Остап Вишня (Митці на прицілі)', 'Гальченко С.', 'До даної книги увійшли лише деякі зразки творчої спадщини «короля» українського гумору XX ст. Остапа Вишні (табірний щоденник із Ухтпечлагу, листи із заслання). Архівно-слідча справа (публікується вперше) віддзеркалює типовий процес творення міфів про наявність ворогів народу у тогочасному суспільстві і приписування їм намагання знищити керівників компартії і держави. Комплекс архівних матеріалів із двотомної справи-формуляра Остапа Вишні — це накопичення донесень секретних агентів спецорганів, що свідчить про боротьбу системи із митцями, найталановитіші з яких зазнали репресій і знищення.', 1, 236, 'Остап_Вишня.jpeg', 5, 0),
(64, 'Оперний світ Соломії Крушельницької: путівник по операх з репертуару співачки', 'Зубеляк М.', 'Пропоноване науково-популярне видання знайомить з оперним репертуаром славетної української співачки Соломії Крушельницької (1872-1952), що налічував майже бо опер різних національних шкіл і напрямів. У лаконічній формі подано інформацію про композиторів, їхні твори, сюжет опери, а також -вміщено уривки з рецензій на виступи С. Крушельницької в оперних партіях. Видання адресоване музикознавцям, театрознавцям, широкому колу шанувальників оперного мистецтва.', 1, 231, 'Соломія_Крущельницька.jpeg', 5, 0),
(65, 'Герой імперії', 'Міллард К.', 'Вінстон Черчилль у 24 роки був переконаний, що досягне абсолютної вершини в політиці. Його амбітність, цілеспрямованість і впевненість у собі змушували перейнятися цією думкою інших. «Мені він не подобається, але одного дня він стане прем\'єр-міністром Великої Британії», — сказав британський офіцер Джордж Байт про юного Черчилля. Однак Вінстону знадобилося сорок років, щоб проторувати свій шлях до найвищого політичного кабінету. У «Герої імперії» йдеться про період Англо-бурської війни — етап, який, за словами Черчилля, став фундаментом його майбутнього життя.', 1, 120, 'Герой_імперії.jpeg', 5, 0),
(66, 'Коко Шанель (Біографія століття)', 'Гідель Анрі', 'Один із найбільш відомих арт-критиків Франції — про жінку, яка створила не тільки нечувані моделі одягу, драпіровок, парфумів, прикрас, але й унікальний стиль, що вплинув на кращих дизайнерів XX і XXI століть. Книга Анрі Гіделя спирається на солідний фактичний матеріал: інтерв\'ю із сучасниками, документи, публікації, втім, це не документальна оповідь, а добротний роман про Габрієль Шанель, ту саму «Коко», чий вік практично нікому не відомий...', 1, 205, 'Коко_Шанель.jpeg', 5, 0),
(67, 'Усі твори в одному томі / Леся Українка /', 'Леся Українка', 'У книжці вперше в історії української літератури вміщено всю відому творчу спадщину славетної української письменниці Лесі Українки, яка спромоглася напрочуд сильно відтворити могутній творчий дух свого народу.', 1, 330, 'Леся_Українка.jpeg', 6, 0),
(68, 'Цеглина', 'Федюк Тарас', '«Цеглина» — вибране одного з найважливіших українських поетів XX–XXI століть Тараса Федюка. Книга містить вірші, написані упродовж останніх п’ятдесяти років: від ранньої, в дечому наївної лірики, до нових іронічно-скептичних текстів. Діалог з пам’яттю, пошуки своєї землі, втеча і неможливість втечі — основні мотиви поезії Федюка. «Цеглина» — як наріжний камінь, як стіна із зітертими фресками, що проступають, ледь засвітить сонце, як глина, з якої виліплено світ.', 1, 299, 'Цеглина.jpeg', 6, 0),
(69, 'Енеїда. Подарункова', 'Котляревський І', 'Колекційне подарункове видання знаменитої «Енеїди» - поеми про пригоди моторного парубка-козака Енея на шляху від Трої до Латинського краю. Читачі зможуть ознайомитися із маловідомими автентичними ілюстраціями, культурологічними та історичними коментарями, зануритися у славну та героїчну козацьку епоху, познайомляться із дотепним українським гумором. Нехай звитяга козака Енея надихає та підбадьорює українців у складні часи та не дозволяє втрачати надію. Видання відзначається елітним поліграфічним виконанням, тиснення обкладинки та бронзове фарбування виготовлено ручним способом. Книга отримала звання «Найкращої книги 22 Форуму видавців» у Львові 2015 року. Гарний подарунок школярам та шанувальникам українського слова.', 1, 280, 'Енеїда.jpeg', 6, 0),
(70, 'Пригоди Клима Кошового, 1908-1913: романи', 'Кокотюха А. В.', 'Андрій Кокотюха — сучасний український письменник, сценарист, журналіст. Автор 50 художніх, документальних та науково-популярних книжок. «Адвокат із Личаківської» — перший із циклу ретродетективів автора, місцем дії якого обрано Львів. 1908 рік. Молодий киянин Клим Кошовий дивом вирвався з тюрми й тікає від переслідувань царської влади до Львова. Але й тут його заарештовує поліція — біля трупа відомого адвоката Євгена Сойки. Покійний мав сумнівних друзів та могутніх ворогів. Самогубство чи вбивство? Правда приголомшить Клима та його відданого друга Йозефа ПІацького. «Привид із Валової» — друга книга серії. 1909 рік. У кишені трупа, знайденого у будинку на вулиці Валовій, виявили візитівку Клима Кошового. Офіційно смерть сталася через нещасний випадок. Але той дім має у Львові недобру славу: кажуть, у ньому вже понад сто років блукає привид Чорної пані... «Автомобіль із Пекарської» — третій роман із циклу про Кошового. 1911 рік. У центрі міста у власному розкішному авто знайдено мертвою доньку нафтового магната. У вбивстві підозрюють її таємного коханця. Кпим разом з Йозефом Шацьким розкривають брудні таємниці з життя міської аристократії, і таке розслідування категорично не подобається впливовій красуні Магді Богданович...', 1, 277, 'Пригоди_клима.jpeg', 6, 0),
(71, 'Василь Стефаник. Твори', 'Василь Стефаник', 'Сьогодні Україна перебуває у стані неоголошеної війни зі своїм східним сусідом, тому ми вирішили взяти за основу нашого видання книгу видатного українського новеліста Василя Стефаника, яка вийшла у Львові 1942 р., під час війни. Її упорядником і автором передмови був син письменника, який «заховався» під псевдонімом Юрій Гаморак. Щоб читачі відчули непідробне первісне звучання Стефаникового слова, подаємо тексти за редакцією його сина. Видання проілюстровано графікою відомого українського художника, лауреата Шевченківської премії Івана Остафійчука.', 1, 267, 'Стефаник.jpeg', 6, 0);
INSERT INTO `books` (`id`, `name`, `author`, `description`, `status`, `price`, `image`, `categoryid`, `featured`) VALUES
(72, 'Українська модерна проза: антологія', 'Винничук В. П.', 'Антологія «Українська модерна проза» представляє майже невідомі твори переважно забутих письменників початку XX сторіччя — Марка Черемшини, Євгена Мандичевського, Василя Пачовського, Івана Липи, Євгенії Ярошинської та багатьох інших. Щоб якнайповніше зазнайомити читачів з працями саме цих, забутих митців, поза увагою залишені хрестоматійні модерністські творіння класиків нашої літератури — Михайла Коцюбинського, Івана Франка, Ольги Кобилянської, Володимира Винниченка. Також обійдено увагою українську модерністську поезію, оскільки вона практично вся вже була опублікована як в окремих авторських збірках, так і в антологіях (зокрема в книгах «Невідоме Розстріляне Відродження» та «Львівська Антологія», які вийшли друком у видавництві «Фоліо»). Переважна більшість оповідань та повістей, які війшли до цієї антології, походить з давніх часописів — «Літературно-Наукового Вістника», «Української Хати», «Ілюстрованої України», «Будучини», «Буковини», «Шляху», «Шляхів Мистецтва» та інших. Завершують книгу спогади Петра Карманського про львівську богему; Галини Журби, Павла Богацького та Клима Поліщука — про київську. Справжнім відкриттям для читачів стане публікація дуже відвертої автобіографічної повісті Клима Поліщука «Світ червоний», де зображені відомі київські поети і прозаїки 1917—1919 років. На жаль, ранній період українського модерну тривав не довго. Перша світова війна внесла свої корективи, а нова доба вимагала вже інших творів.', 1, 255, 'Модерна_проза.jpeg', 6, 0),
(73, 'Музей покинутих секретів: Роман', 'Забужко О.', 'Цей роман визнано українською та зарубіжною критикою не тільки за найвидатніший твір української літератури доби незалежності, а й за один із найважливіших у всій літературі Східної Європи після падіння комунізму. Нагороджений Центральноєвропейською літературною премією «Анґелус» (2013), перекладений англійською, німецькою, польською, чеською, російською мовами, неодноразово відзначуваний як «книжка року» (в Україні, Німеччині, Швейцарії, Польщі), «Музей покинутих секретів», «роман нобелівського класу» (Newsweek Polska); по праву став візитною карткою нової української літератури. Це — сучасний епос сучасної України: родинна сага трьох поколінь, події якої охоплюють період від 1940-х років до весни 2004-го. Велика література і жорстока правда — про владу минулого над майбутнім, про кохання, зраду і смерть, про споконвічну війну людини за право бути собою.', 1, 218, 'Музей_покинутих_секретів.jpeg', 6, 0),
(74, 'Майдан. Революція духу. Каталог-альманах', 'Мухарський А.', 'Великий культурологічний альманах, в якому зібрано свідчення очевидців, публіцистичні статті та есе українських письменників і філософів, науковців і митців, присвячені знаковим подіям української національно-визвольної революції в період з листопада 2013-го до лютого 2014 року. Видання ілюстроване близько 200-ми світлинами авторства українських фотографів. Альманах є однією з найперших спроб якнайповніше висвітлити, осягнути, осмислити історичні, філософські й культурні аспекти та проблематику революційних днів. Видання розраховане на широку читацьку аудиторію.', 1, 216, 'Майдан.jpeg', 6, 0),
(75, 'Хіба ревуть воли, як ясла повні?', 'Мирний П.', 'В історію української літератури видатний письменник Панас Мирний (1849—1920) увійшов як майстер психологічної прози. Його славетний роман «Хіба ревуть воли, як ясла повні?» вперше вийшов друком у Женеві 1880 р. За своїм жанром це багатоплановий соціально-психологічний роман, у якому глибокий соцішіьний аналіз дореформеного та пореформеного села поєднано з менш глибоким розкриттям внутрішнього світу і психології. В образі головного героя Чіпки письменник намалював «безталанну дитину свого віку, скаліченого виводка свого побуту, пригніченого усяким панством...»', 1, 209, 'Ревуть_воли.jpeg', 6, 0),
(76, 'І знов я влізаю в танк', 'Забужко О.', 'Нова збірка актуальної публіцистики відомої письменниці - це книга для тих, хто не уникає чесних відповідей на нагальні питання і переймається викликами, які ставить перед українцями сьогодення. Тут ідеться про химери «поневоленого розуму» та про нові маски й форми, яких набуває в епоху мас-медіа давня війна, про пошук героїв, про знакові постаті й тексти, а зрештою про те, як гідність і спротив нав’язаним правилам гри дають нову надію європейській цивілізації. Це ексклюзивний репортаж із гарячих точок інформаційного фронту, де триває боротьба з забуттям і байдужістю, які мало не коштували нам держави.', 1, 198, 'В_танк.jpeg', 6, 0),
(77, 'Антена', 'Жадан Сергій', '«Антена» - 80 нових віршів, написаних автором упродовж останніх двох років. Невідомо, на чому тримається чутливість «Антени», але це 80 спроб упіймати коливання повітря, вловити перетікання в просторі невидимих радіохвиль, відчути на дотик час, у якому ми живемо, яким ми дихаємо, який ми проговорюємо. Час, кожен доторк до якого лишає опік. Час, у якому приватні щоденникові записи можуть виявитися воєнною хронікою, а біблійні історії - ранковими новинами.', 1, 190, 'Антена.jpeg', 6, 0),
(78, 'Б-52: роман', 'Дністровий А.', '«Б-52» Анатолія Дністрового — дотепний і динамічний роман про чоловіка в кризі середнього віку. Досить неординарний за натурою і стилем життя, успішний топ-менджер, аналітик і драматург Мирослав Шефер наражається на любов до юної анархо-феміністки, і це руйнує його колишній світ.', 1, 175, 'Б-52.jpeg', 6, 0),
(79, 'Фантастичні звірі і де їх шукати', 'Ролінг Дж. К.', 'Дж.К. Ролінґ запрошує вас до нової ери Чаклунського Світу...\r\nДослідник і магізоолог Ньют Скамандер щойно завершив навколосвітню подорож у пошуках найрідкісніших і найне-звичніших магічних істот. Прибувши до Нью-Йорка, він не має наміру тут затримуватись. Одначе, коли йому випадково підмінюють валізу і деякі його фантастичні звірі розбігаються по місту, під загрозою опиняються всі...\r\n«Фантастичні звірі і де їх шукати» — сценарний дебют Дж.К. Ролінґ, авторки серії улюблених світових бестселерів про Гаррі Поттера. За цим сценарієм було знято успішний фільм з володарем «Оскара» Едді Редмейном у ролі Ньюта Скамандера. Дія цієї захопливої історії про дружбу, чари, дивовижних персонажів і грандіозну веремію у Нью-Йорку відбувається за півстоліття до появи Гаррі. Це одна з найкращих і найдинамічніших пригодницьких епопей, яка ще матиме своє продовження...', 1, 435, 'Фантастичні_звірі.jpeg', 7, 0),
(80, 'Бджоли', 'Соха П.', 'Якщо всі бджоли раптом зникнуть, вже через кілька років все людство почне потерпати від голоду - такий один із поширених сценаріїв кінця світу. Так книжка Пйотра Сохи «Бджоли» не про те, що наш величезний світ тримається на тендітних комашиних крильцях. Вона просто про бджолине життя-буття від часів динозаврів і до сьогодні. Перевірені наукові факти викладено доступно й цікаво, з безліччю кумедних ілюстрацій.', 1, 410, 'Бджоли.jpeg', 7, 0),
(81, 'Наука', NULL, 'Яскрава енциклопедія з чудовими ілюстраціями незамінна для юних дослідників та майбутніх науковців!. У цій енциклопедії розповідається про сучасні дослідження у різних галузях науки. Ідеться, зокрема, про вивчення процесів, що відбуваються в людському організмі, у надрах нашої планети, на її поверхні та в неосяжних просторах Всесвіту. Енциклопедія містить приголомшливі наукові факти і сотні ілюстрацій. Для широкого кола читачів.', 1, 380, 'Наука.jpeg', 7, 0),
(82, 'Лицарі (комікси)', NULL, 'Року Божого 1012 у землях короля Луїслабка троє братів вирушають у мандрівку, щоб стати Королівськими Лицарями. І один з цих братів — ти. Але Лицарем стати не просто! Тобі доведеться перетнути засніжені гори, похмурі ліси й смердючі болота, аби знайти Браслети Відваги. На цьому шляху ти можеш зустріти ницих розбійників, вогнедишних драконів, старих чаклунів та моцних воїнів. Тобі доведеться розгадувати загадки, вивчати нові здібності та збирати цінні предмети. Успіх тепер залежить тільки від тебе, адже герой — це ти! «Лицарі» — це комікс-гра. Те, як розгортається історія, залежить виключно від вибору гравця. У вас на руках буде Аркуш Персонажа, де треба занотовувати вміння персонажа, кількість монет, наявні предмети тощо. Процес гри полягає в тому, що читач має вибирати номер панелі, на яку він хоче пересунути персонажа й вирішувати загадки. Але обирати треба з розумом, адже неправильний вибір може призвести до поразки!', 1, 323, 'Лицарі.jpeg', 7, 0),
(83, 'Каю. Комплект з 6 книжок. Мої історії на ніч', 'Саншагрен Жосалін', 'Після клопітливого дня усім потрібно розслабитися — навіть Каю. У цій коробці з історіями на ніч міститься шість оповідок, які залюбки прослухає перед сном кожен малюк.', 1, 299, 'Каю.jpeg', 7, 0),
(84, 'Програмування для дітей. HTML. CSS та JavaScript', 'Вітні Девід', 'Майстерний гід із програмування для початківців. Із ним ти створиш власний сайт, прикладну програму та гру, розів’єш реальні навички майбутнього програміста. Вчись, слідуючи крок за кроком докладним інструкціям до шістьох карколомних місій. А потім перевір свої знання й навички, створивши власний проект.\r\n\r\n', 1, 291, 'Програмування.jpeg', 7, 0),
(85, 'Велика енциклопедія чомусика в запитаннях і відповідях', NULL, 'Книжка — чудова знахідка для маленьких чомусиків та їхніх батьків!\r\nТут наведені не лише відповіді на найнесподіваніші дитячі запитання, але й багато цікавої пізнавальної інформації, яскраві ілюстрації, несподівані факти. Загадки Всесвіту, природні явища, флора і фауна, історичні події та пам’ятки, наукові відкриття — про все це розповідає енциклопедія. У ній викладено найновіші та найцікавіші відомості з фізики та хімії, історії та географії, біології та комп’ютерних наук. Численні малюнки і фотографії зроблять інформацію більш наочною, а читання — цікавішим.', 1, 290, 'Енциклопедія.jpeg', 7, 0),
(86, 'Велика ілюстрована книга казок', NULL, 'Книжка з таким щедрим ілюструванням — велика рідкість. Чудові казки українських та іноземних письменників з розкішними ілюстраціями провідних українських художників.', 1, 286, 'Книга_казок.jpeg', 7, 0),
(87, 'Маленький принц', 'Сент-Екзюпері А.', 'Видатний французький письменник Антуан де Сент-Екзюпері був відважним льотчиком. Одного разу на своєму маленькому поштовому літаку він навіть перелетів Атлантичний океан. Це був світовий рекорд. Якось письменник зазнав аварії над Лівійською пустелею. Доки він десять днів ремонтував свій літак, до нього навідувався маленький пустельний лис. Вони заприятелювали. Згодом після тієї пригоди Екзюпері написав свій найгеніальніший твір — казку «Маленький принц»...', 1, 261, 'Маленький_принц.jpeg', 7, 0),
(88, 'PYTHON для дітей. Веселий вступ до програмування', 'Бріггс Дж. Р.', 'Програмуйте із задоволенням! Ця книжка легко та невимушено допоможе вивчити основи однієї з найпопулярніших сучасних мов програмування – Python, що завдяки простому і зрозумілому синтаксису особливо пасує для новачків. Крок за кроком, на простих та дотепних прикладах та з допомогою смішних завдань ви зможете пізнати тонкощі мови Python. Навчитеся використовувати основні елементи цієї мови – списки, функції, модулі, малювати вбудованими інструментами Python та анімувати зображення і навіть зможете написати дві справжні комп’ютерні гри: забавку з м’ячем «Скок!» та бродилку «Містер Руки-палички біжить до виходу».', 1, 260, 'python.jpeg', 7, 0),
(89, 'Пригоди Аліси в Дивокраї (Світ чарівних казок)', 'Керролл Л.', 'Ця книга сповнена чарівництвом і дивами — саме тим, чого нам так не вистачає в житті. Оригінальні герої, парадоксальні ситуації, цікаві діалоги — все це робить пригоди Аліси незабутніми. А завдяки позначеному іскристою фантазією майстерному перекладу і прекрасним ілюстраціям молодої художниці Євгенії Чистотіної ця книжка стане і справжньою окрасою домашньої бібліотеки, і прекрасним подарунком.', 1, 256, 'Пригоди_Аліси.jpeg', 7, 0),
(90, 'Діти капітана Гранта: роман (Скарби: Молодіжна серія)', 'Жуль Верн', 'Роки життя Жуля Верна (1828—1905) припали на період визначних географічних відкриттів та небачених досягнень у науці й техніці. Тож, ступаючи на літературну ниву, він мріяв створити жанр, досі не відомий читацькому загалу — \"роман про науку\". Письменника вабила здатність науки розширити можливості людини, відкрити їй досі незнані куточки планети і навіть нові світи. \"Діти капітана Гранта\" — роман географічний. Яскравими фарбами у ньому змальовано природу Південної Америки, Австралії й Нової Зеландії, екзотичних тварин та рослини. Ще одна особливість роману — вміння Жуля Верна тонко вплести у сюжет взаємовідносини людей різних культур, яких мандрівники зустрічають на своєму шляху, що його так майстерно проклав автор.', 1, 254, 'Діти_капітана_Гранта.jpeg', 7, 0),
(91, 'Основи патології за Роббінсом: переклад 10-го англ.вид.: у 2 т. — Т. 1', 'Вінней Кумар', 'Книга «Основи патології за Роббінсом» належить до перевіреної часом англомовної серії медичних видань Роббінса і Котрана. В її підготовці брала участь професійна команда редакторів, що гарантує глибоке розуміння найважливіших понять патології. З використанням численних ілюстрацій наведено стислий огляд основних принципів патології людини, що ідеально відповідає сучасним потребам студентів. Десяте видання повністю перероблено, основну увагу приділено патогенезу і клінічній картині захворювань. Клінічні теми розширено й оновлено. До ілюстративного матеріалу додано нові малюнки та схеми для унаочнення основних патологічних процесів. Завдяки сучасним програмам матеріал проілюстровано якісними мікрофотографіями, зображеннями макропрепаратів і рентгенівськими знімками. Для зручності основну інформацію оформлено у вигляді окремих блоків із маркованими списками. По всьому тексту виділено відомості про патогенез, морфологію та патофізіологію.\r\nДля студентів медичних закладів вищої освіти, лікарів-інтернів, лікарів-практиків.', 1, 940, 'Патологія.jpeg', 8, 0),
(92, 'Онкологія. Підручник. 2-ге вид.', 'Бондар Г. В.', 'Національний підручник відповідає вимогам навчальної програми з онкології. Особливу увагу приділено найважливішим і актуальним питанням вітчизняної та світової онкології, сучасним методам ранньої і доклінічної діагностики онкозахво-рювань. Розглянуто питання патогенезу клінічних проявів пухлин основних локалізацій, методи комбінованої терапії з урахуванням сучасних досягнень, методики реабілітації онкологічних хворих і профілактики раку. Для студентів вищих медичних навчальних закладів IV рівня акредитації, студентів-інтернів, лікарів-онкологів, сімейних лікарів.', 1, 640, 'Онкологія.jpeg', 8, 0),
(93, 'Педіатрія Підручник для студентів вищих медичних закл IV рівня. Вид. 5-те', 'Тяжка О. В.', 'У підручнику представлено матеріали з пропедевтики педіатрії (розділ І), найбільш поширених дитячих соматичних хвороб (розділ II), дитячих інфекційних хвороб (розділ III) та дитячого туберкульозу (розділ IV). Тематика підручника викладена у межах навчальної програми для студентів медичного факультету, які вивчають педіатрію на 111—\"V1 курсах. До кожної представленої теми подані питання, які мають бути засвоєні студентом при вивченні даного матеріалу, і тестові завдання з їх вирішенням для контролю засвоєння теми. Підручник рекомендується для студентів вищих медичних закладів Ш-ІУ рівнів акредитації, а також для інтернів і лікарів.', 1, 593, 'Педіатрія.jpeg', 8, 0),
(94, 'Анатомія людини Т3. Вид. 6-те вид.', 'Головацький А. С.', 'У третьому томі підручника подані сучасні дані про будову периферійної нервової системи та серцево-судинної системи. Допоможуть у кращому засвоєнні матеріалу зведені таблиці. В кінці кожного розділу підручника є питання для повторення і самоконтролю, які дозволяють краще зрозуміти прочитане. Усі анатомічні терміни подані українською і латинською мовами і відповідають Міжнародній анатомічній номенклатурі (Сан-Пауло, 1997), українському стандарту (Київ, 2001). Підручник призначений для студентів та викладачів вищих медичних навчальних закладів IV рівня акредитації, складений за кредитно-модульним принципом відповідно до вимог Болонського процесу.', 1, 575, 'Анатомія.jpeg', 8, 0),
(95, 'Медсестринство у внутрішній медицині. Підручник 5-те вид.', 'Стасишин О. С.', 'Підручник відповідає типовій програмі для вищих медичних навчальних закладів І—III рівнів акредитації. Матеріал викладено відповідно до сучасних вимог, що висуваються до підручників. Зокрема узагальнено відомості про розвиток внутрішньої медицини як науки, наведено короткі історичні відомості, принципи організації і методи надання лікувально-профілактичної допомоги пацієнтам в Україні, а також визначення теоретичних положень, що стосуються таких понять, як «хвороба», «симптоми», «проблеми», «медсестринський діагноз». Дається характеристика методів обстеження пацієнта медичною сестрою. Акцентовано увагу на функції медичної сестри відповідно до етапів медсестринського процесу. Описано основні захворювання внутрішніх органів, починаючи з анатомо-фізіологічних особливостей кожної системи, клінічні ознаки, основні проблеми пацієнта, планування медсестринських втручань, основні принципи лікування, диспансеризацію, профілактику. Для студентів вищих медичних навчальних закладів І—III рівнів акредитації.', 1, 475, 'Медсестринство.jpeg', 8, 0),
(96, 'Анестезіологія та інтенсивна терапія: підручник 2-е вид.', 'Палій Л. В.', 'Підручник розроблено відповідно до нової навчальної програми. Курс анестезіології та інтенсивної терапії закладає основи вивчення студентами невідкладних станів, при яких застосовують методи знеболювання й інтенсивної терапії. Опанування цих дисциплін інтегрується з вивченням внутрішньої медицини, педіатрії, фармакології, оскільки ґрунтується на набутті знань з клінічної фізіології дихальної, серцево-судинної систем, печінки, нирок тощо. У другому виданні автори осучаснили деякі розділи підручника з урахуванням суттєвих змін, що відбулися за останні роки в медичній галузі. Згідно з новою програмою, у підручник уведено розділ \"Сепсис, раціональна антибіотикотерапія\". Для студентів медичних закладів вищої освіти.', 1, 450, 'Анестезіологія.jpeg', 8, 0),
(97, 'Інфекційні хвороби. підручник', 'Козько В. М.', 'Підручник підготовлено відповідно до типових навчальних програм для студентів зі спеціальності «Медсестринство» спеціалізацій «Лікувальна справа», «Акушерська справа», «Сестринська справа» напряму підготовки «Охорона здоров\'я» з урахуванням вимог освітньо-кваліфікаційної характеристики, освітньо-професійної практичної підготовки та стандартів освіти на основі нормативно-директивних матеріалів МОЗ України. Нозологічні форми викладено за однотипною схемою: визначення, етіологія, епідеміологія, патогенез, клінічна картина, ускладнення, діагностика, диференціальна діагностика, лікування, профілактика. Зокрема висвітлено теми, які виносять на самостійне позааудиторне опрацювання. Особливу увагу приділено питанням загальної інфектології, догляду за хворими, наданню невідкладної допомоги. У підручнику використано матеріали та рекомендації ВООЗ, дані вітчизняних і зарубіжних публікацій провідних фахівців, клінічні протоколи надання медичної допомоги МОЗ України. Поданий матеріал викладено на сучасному науковому рівні відповідно до чинних міжнародних рекомендацій. Для студентів медичних закладів вищої освіти освітньо-кваліфікаційних рівнів «Бакалавр» і «Магістр» зі спеціальності «Медсестринство» напряму підготовки «Охорона здоров\'я».', 1, 446, 'Інфекційні.jpeg', 8, 0),
(98, 'Захворювання органів дихання в сімейній медицині: навч. посібник', 'Бабінець Л. С.', 'У навчальному посібнику матеріал подано системно, наведено короткі відомості щодо основ пропедевтики захворювань органів дихання, описано синдроми і найпоширеніші захворювання дихальної системи, міжнародні алгоритми ведення таких пацієнтів, а також рекомендації щодо медикаментозної та немедикаментозно! їх реабілітації. Особливістю видання є включення до його контенту рекомендацій фінського медично-наукового товариства Duodecim Medical Publications Ltd, яке спеціалізується на комплексних рішеннях у сфері доказової медицини. Для широкого кола фахівців первинної медичної допомоги (лікарів загальної практики — сімейних лікарів, дільничних терапевтів і педіатрів), а також лікарів інших медичних спеціальностей — терапевтів, пульмонологів, які практикують у галузі амбулаторної терапії та пульмонології, студентів-медиків старших курсів, лікарів-інтернів, а також усіх, хто потребує підвищення кваліфікації у сфері ведення пацієнтів із захворюваннями дихальної системи на основі як класичних, так і останніх сучасних вітчизняних і міжнародних рекомендацій.', 1, 383, 'Захворювання_органів_дихання.jpeg', 8, 0),
(99, 'Фармакологія: підручник. 8-ме вид. переробл. та доп.', 'Нековаль І. В.', 'У сьомому виданні підручника містяться дані про сучасні лікарські засоби та загальні правила рецептури відповідно до вимог Болонської конвенції щодо реформування системи освіти. При його підготовці дотримано принципів фундаменталізації, міждисциплінарного зв\'язку та проблемності. Відповідно до нової навчальної програми викладено найважливіші розділи фармакології. Наведено класифікацію та характеристику сучасних основних фармакологічних груп і препаратів, лаконічно і доступно висвітлено питання фармакокінетики і фармакодинаміки лікарських засобів. У кожній темі наведено повну характеристику основного лікарського засобу певної групи та порівняльну характеристику інших. Для полегшення запам\'ятовування правильної назви препаратів, особливостей їхнього застосування, терапевтичних та вищих доз, а також належності до списків «А» і «Б» у підручнику після кожного розділу представлено таблицю з відповідними даними. Характерною особливістю підручника є розділи про гомеопатичні лікарські засоби та пробіотики, інформація про особливості роботи персоналу з окремими препаратами кожної фармакологічної групи, а також позначення концентрації розчинів відповідно до Європейської фармакопеї. У зв\'язку з тим що широко використовуються препарати пробіотичних та гомеопатичних лікарських засобів, у перероблене сьоме видання включено додатки з описом препаратів цих груп. Підручник містить запитання, тестові завдання та задачі для самопідготовки студентів. Професіональні задачі для студентів фармацевтичних факультетів виділено спеціальною позначкою (*). Для студентів вищих медичних навчальних закладів І—III рівнів акредитації за спеціальностями «Фармація», «Сестринська справа», «Акушерська справа».', 1, 370, 'Фармакологія.jpeg', 8, 0),
(100, 'Посібник з біологічної хімії «Крок 1. Стоматологія»: Навч. посіб.', 'Яніцька Л. С.', 'Навчальний посібник включає тестові завдання, що були представлені в екзаменаційних буклетах \"Крок-1\" затверджені базові тестові завдання центру тестування МОЗ України різних років, а також тести, створені викладачами кафедр медичних закладів вищої освіти України. Тестові завдання згруповані за темами, до кожного тестового завдання наведена правильна відповідь та подано її обґрунтування. Самостійна робота з тестовими завданнями посібника сприятиме поліпшенню якості вивченого матеріалу та підготовки студентів до складання ліцензійного інтегрованого іспиту \"Крок 1. Стоматологія\" Для студентів медичних закладів вищої освіти за спеціальністю \"Стоматологія\".', 1, 343, 'Стоматологія.jpeg', 8, 0),
(101, 'Психіатрія та наркологія в загальній медичній практиці: Навч. посіб.', 'Кожина Г. М.', 'У навчальному посібнику наголошено на важливості вивчення психіатрії в курсі медичної освіти з огляду на те, що на лікарів загальної практики припадає найбільша частка первинних звернень пацієнтів із психічними розладами. Висвітлено основи вітчизняного законодавства в галузі психіатрії та принципи організації надання психіатричної і медико-психологічної допомоги особам із різними видами психічних та поведінкових розладів. Акцентовано увагу на медичних і соціальних проблемах вживання психо-активних речовин. Для студентів медичних закладів вищої освіти, лікарів-інтернів та лікарів загальної практики — сімейної медицини.', 1, 337, 'Психіатрія.jpeg', 8, 0),
(102, 'Натуральне вино', 'Лежерон І.', 'Процес виготовлення вина стає дедалі більш ненатуральним: починаючи від розбризкування пестицидів на виноградниках, і до надмірного застосування сульфітів і домішок у погребі. Проте, на щастя, існує інший спосіб виноробства, про який розповідає Ізабель Лежерон. Вона популяризує натуральне вино у всьому світі та проводить надзвичайно успішну виставку вина «RAW WINE» в Лондоні, Берліні, Нью-Йорку і Лос-Анджелесі. В цій книжці Ізабель пояснює, чому найкращі та автентичні вина виготовляють саме натуральним способом.\r\n', 1, 519, 'Натуральне_вино.jpeg', 9, 0),
(103, '1-ша українська загально-практична кухня.', 'Франко О.', 'Книга Ольги Франко «Перша українська загально-практична кухня» одразу після виходу в світ 1929 року стала дуже популярною. її зачитували до дірок. Кожна галицька господиня вважала за необхідне мати її на своїй кухні. Сьогодні книга Ольги Франко повертається до читача у своєму первозданному варіанті, не обкраяна і не перекручена, у всій автентичності її порад, рекомендацій та рецептів. Вона отримує своє «друге» дихання завдяки коментарям відомої львівської кулінарної блоґерки Маріанни Душар, дослідниці галицької кулінарної традиці.', 1, 450, 'Укр_кухня.jpeg', 9, 0),
(104, 'Bread Street Kitchen. 100 рецептів смачних ресторанних страв для приготування вдома', 'Рамзі Г.', 'Для тих, хто цінує вишукані ресторанні страви і водночас надає перевагу приготуванню їжі вдома, пропонуємо рецепти страв, що є ідеальними не лише на сніданок, обід і вечерю. Скориставшись ідеями та порадами відомого шеф-кухаря, можна швидко й без зусиль накрити стіл до пізнього сніданку, званого обіду чи, скажімо, романтичної вечері. Їжа, як відомо, віддзеркалює стиль життя, тож кожен із нас шукає рецепти, які відповідають саме його уявленням про раціональне харчування, про корисність і смакові якості страв. Допомогти у цій непростій справі покликане наше видання.\r\n\r\nГордон Рамзі - британський шеф-кухар шотландського походження, перший шотландець, який отримав одразу три зірки Мішлен! Також він - ведучий вельми успішних шоу, автор кулінарного шоу \"Пекельна кухня\".', 1, 436, 'Гордон_Рамзі.jpeg', 9, 0),
(105, 'Готуємо по-японськи', 'Канеко Е.', 'Поціновувачів східної кухні щороку стає дедалі більше. Проте щоб насолодитися оригінальною їжею, зокрема стравами японської кухні, вам не обов\'язково відвідувати ресторани. Маючи під рукою цю книгу, ви швидко навчитеся готувати автентичні страви країни, де сходить сонце, і вони по-справжньому урізноманітнять ваше повсякденне та святкове меню.', 1, 272, 'Готуємо_по-японськи.jpeg', 9, 0),
(106, 'Україна. Захід. Схід.: путівник', 'Тичина А. Г.', 'Путівник «Україна. Центр. Південь» розрахований як на мешканців України, так і на людей, що живуть за її кордонами, які люблять самостійно планувати подорожі, відкрииати для себе ноні маршрути і місця. З його допомогою можна освоїти до ЗО захоплюючих маршрутів Центральною і Південною Україною, дізнатися про цікаві місця, музеї та пам\'ятники за маршрутом слідування, вибрати час туру, щоб не тільки насолодитися новими відкриттями, а й відвідати місцеві свята та фестивалі. Автор цієї книжки за кермом машини проїхав тисячі кілометрів і розповів не тільки про пам\'ятки і пам\'ятники, а й про стан доріг, якими туристам доведеться добиратися до мети подорожі. Крім того, автор надає докладну інформацію про музеї, готелі, місця відпочинку та розваг, що, безумовно, полегшує планування подорожі. Книжка ілюстрована фотографіями автора.', 1, 255, 'Путівник.jpeg', 9, 0),
(107, 'Великий сучасний довідник бджоляра: 15 000 кор.порад', 'Білик Е. В.', 'Вашій увазі пропонується великий сучасний довідник бджоляра. На його сторінках подаються відомості про життя бджолиної сім\'ї, облаштування пасіки, сезонні пасічні роботи, про боротьбу із хворобами і шкідниками бджіл, одержання меду та інших цінних продуктів бджільництва. При створенні книжки враховано останні тенденції сучасного бджільництва. Корисні рекомендації професіоналів стануть у пригоді як початківцям, так і досвідченим бджолярам. Адресовано широкому читацькому загалу.', 1, 195, 'Довідник_бджоляра.jpeg', 9, 0),
(108, 'Випічка. Десерти (Подарункова)', 'Карпенко Ю. М.', 'Книга «Випічка. Десерти» допоможе вам відкрити секрети кондитерської майстерності. На сторінках цього видання ви знайдете не тільки різноманітні рецепти випічки та десертів, а й безліч корисних порад і відомостей: способи замішування кожного із запропонованих різновидів тіста, рецепти глазурі та помадок, кремів, сиропів, просочення, технології випічки і прикрашання для пирогів, тортів і тістечок, а також час і складність приготування. А докладний покроковий опис і барвисті ілюстрації допоможуть навіть починаючому кондитерові легко орієнтуватися в процесі приготування і перетворити свої ідеї та фантазії на справжній твір мистецтва.', 1, 180, 'Випічка.jpeg', 9, 0),
(109, 'Солнечный дом. Естественное освещение.', 'Косо Й.', 'Предлагаем вашему вниманию очередной том из серии изданий, объединенных названием \"Дизайн и технология\" венгерского автора Йожефа Косо.\r\n\r\nВ книге содержится много полезных советов для тех, кто ценит изысканность и обладает тонким эстетическим вкусом; поможет она и непрофессионалам - восприимчивым к красоте и мечтающим о хорошем, оберегающем наше здоровье доме; а еще она будет интересна домовладельцам, строителям и архитекторам-практикам, ведь здесь приводится множество убедительных примеров, даются профессиональные советы и рекомендации.', 1, 186, 'Сонячний_дім.jpeg', 9, 0),
(110, 'Самые распространенные комнатные растения.', 'Лонгман Д.', 'Самые распространенные комнатные растения Выращивание комнатных растений - одно из самых любимых увлечений и городских жителей, и сельчан. Всем известно, что при желании в обычной квартире можно создать настоящий зимний сад. Но одного желания мало. Чтобы цветы радовали глаз, нужно уметь многое: и правильно их поливать, и удобрять, и пересаживать, и оберегать от болезней. В книге Дэвида Лонгмана - всемирно известного цветовода вы найдете все необходимые рекомендации по уходу за комнатными растениями с подробным описанием их особенностей. Красочные иллюстрации дополняют информационный материал, что поможет вам добиться тех результатов, на которые вы рассчитываете.', 1, 167, 'Рослини.jpeg', 9, 0),
(111, 'Гроші країн світу. Банкоти країн Європи', 'Лоза В.', 'Альбом для стікерів, який дозволяє зібрати унікальну колекцію банкнот різних країн світу.', 1, 160, 'Гроші.jpeg', 9, 0),
(112, 'Замки Тернопільщини', 'Ягодницька М. О., Стропець Б.С.', 'Тернопільщина — унікальний край, розташований на перехресті шляхів із заходу на схід, з півночі на південь. Через те споконвіків він перебував у вирі подій, пов\'язаних із європейською історією, зокрема і середньовічною. Саме тому тут було збудоване найбільше замків та фортець і найбільше їх збереглося до наших часів. В книзі розповідається про історію замків, їхню роль у місцевій та європейській історії, наведено нові дані — результати археологічних досліджень, під дещо іншим кутом розглянуто вже відомі факти. І в результаті ви маєте змогу познайомитися з частиною нашої, більш реальної, історії, отримати цікаву інформацію про замки та інші оборонні споруди, розташовані на території Тернопільської області. Видання супроводжується багатим ілюстративним матеріалом.', 1, 161, 'Замки.jpeg', 9, 0),
(113, 'Спорт у тіні імперій', 'Збігнєв Р.', 'За легендою, коли в Стародавній Греції розпочиналися олімпійські ігри, припинялися війни. Відновлюючи традицію олімпіад у сучасності, П\'єр де Кубертен стверджував: «О спорт, ти — мир!» Але часи змінюються, й іноді геть не на краще. Політика і держави грубо втручаються у світ спорту, намагаючись використовувати змагання і самих атлетів у власних інтересах. Надзвичайно цікаві розповіді про складні стосунки влади і спорту, життя видатних спортсменів, змагання, що проходили на тлі подій, які зараз уже історія...', 1, 153, 'Спорт.jpeg', 9, 0),
(114, 'Програмування мовою Java', 'Васильєв Олексій', 'Книга присвячена мові Java, яка на сьогодні є однією з найпопулярніших мов програмування. Це професійна мова, яка дозволяє створювати складні та ефективні програми.\r\nУ книзі розглядаються всі основні теми, котрі формують парадигму мови програмування Java. Серед них, зокрема, базові прийоми створення програм, основні оператори та типи даних, керуючі інструкції, класи та об’єкти, спадкування, перевантаження та переозначення методів, інтерфейси (в тому числі функціональні), лямбда-вирази, обробка помилок, створення потоків, узагальнені типи, робота з файлами, методи створення програм із графічним інтерфейсом.\r\nТеоретичний матеріал поданий у простій і зрозумілій формі та пояснюється на прикладах. Книга буде корисною для всіх, хто вивчає мову програмування Java.', 1, 399, 'Java.jpeg', 10, 0),
(115, 'Програмування мовою Python', 'Васильєв Олексій', 'Книга присвячена мові програмування Python. На сьогодні це одна з найпопулярніших та найперспективніших мов. Вона проста, ефективна і красива. Книга допоможе читачеві познайомитися з мовою Python і навчитися використовувати її для написання професійних програм. Книга містить інформацію про особливості мови Python та необхідне програмне забезпечення. Детально обговорюються основні програмні конструкції, які використовуються при написанні програм. Описуються базові оператори та керуючі інструкції, функції, списки, кортежі, множини, словники і текст. Окрема увага приділяється реалізації в Python парадигми об’єктно-орієнтованого програмування. Теоретичний матеріал ілюструється великою кількістю практичних прикладів. Книга буде корисною для студентів, слухачів курсів, інженерів, викладачів та всіх, хто вивчає мову програмування Python.', 1, 450, 'Python(2).jpeg', 10, 0),
(116, 'Несамовита фізика', 'Варламов А.', 'Усі ми в школі вивчали фізику, але чи знайшли пояснення всім тим дивовижним речам, що нас оточують? Автори цієї книжки, досвідчені науковці Андрій Варламов і Лев Асламазов, виходять за межі традиційного шкільного предмета й із захопленням розповідають про неймовірні явища, з якими ми стикаємося в житті.\r\nЗ книжки ви дізнаєтеся про фізику приготування макаронів і кави, як працює телебачення, чому небо блакитне та багато інших цікавих фактів. Автори роздумують над красою цієї науки й на прикладах показують, чому річки розмивають береги, як зіграти на келихах, чому вам закладає вуха і як усе це пояснити за допомогою звичайних фізичних формул та законів.', 1, 274, 'Фізика.jpeg', 10, 1),
(117, 'Всесвіт на долоні. Подорож крізь простір, час та їхні межі', 'Гальфар К.', 'Всесвіт на долоні»—книжка-подорож, в якій дотепний, грайливий і дещо балакучий гід Кристоф Ґальфар веде читачів на край світу у буквальному сенсі слова: на межу і за межі наших сучасних знань та навіть нашої уяви. Фізик, учень Стівена Гокінга та співавтор книжки Стівена Гокінга та Люсі Гокінг «Джордж і таємний ключ до Всесвіту», Ґальфар просто пояснює найскладніше у фізиці, відкриваючи для широкої аудиторії таємниці далеких галактик, подорожей у часі, народження і смерті зірок, а навіть народження часу і простору.', 1, 238, 'Всесвіт_на_долоні.jpeg', 10, 0),
(118, 'Гіперпростір: наукова одіссея крізь паралельні світи, викривлений простір-час і десятий вимір', 'Кайку М.', 'Чи існують ще виміри, крім звичних трьох просторових і одного часового? Чи можливо подорожувати в часі й змінити минуле? Чи існують паралельні світи? Багато з нас замислювалися над цим, однак донедавна науковці відкидали такі ідеї. Тепер усе змінилося, і ці питання стали темою інтенсивних наукових досліджень. “Гіперпростір” – це наукова одіссея крізь паралельні світи, викривлений простір-час і десятий вимір, а також захоплива розповідь про пошук славнозвісної “теорії всього”, яка б об’єднала всі сили природи і розкрила таємницю походження Всесвіту.\r\nДля широкого кола читачів.', 1, 225, 'Гіперпростір.jpeg', 10, 0),
(119, 'Генплан. Як ДНК робить нас тими,ким ми є', 'Пломін Р.', 'Експерт у галузі генетики поведінки Роберт Пломін розповідає, як сучасна наукова революція перетворила ДНК на інструмент, що дає змогу прогнозувати сильні та слабкі сторони людини від самого її народження. Генетичні дослідження засвідчили, що відмінності ДНК, успадковані від батьків, є тим чинником, що визначає нашу психологічну індивідуальність протягом усього життя, це генеральний план, який робить нас тими, ким ми є. За словами автора, нові уявлення про генетику кардинально змінюють ситуацію. Генетика у значно більшій мірі обумовлює психологічні особливості людини, ніж інші фактори разом узяті. Саме від неї на 50% залежать наші характерні відмінності — не тільки психічне здоров\'я чи, скажімо, здатність до навчання, а й усі психологічні риси, що роблять нас особистостями, визначають наші інтелектуальні здібності. Природа, а не виховання, робить нас тими, ким ми є.', 1, 180, 'Генплан.jpeg', 10, 1),
(120, 'Чорні діри і молоді всесвіти та інші лекції', 'Стівен Гокінг', 'Перед вами збірка наукових нарисів 1976-1992 років. Уперше лекцію під назвою «Чорні діри і молоді всесвіти» науковець прочитав у Каліфорнійському університеті в Берклі. Згодом доповнена лекція стала частиною повноцінної збірки, до якої ввійшли 13 оповідей та розгорнуте інтерв’ю науковця для передачі «Диски безлюдного острова». Книжка є своєрідним продовженням діалогу з читачем, розпочатого ще у «Короткій історії часу». Найвідоміший астрофізик сучасності тлумачить найбільш імовірні моделі устрою Всесвіту, розмірковує про уявний час, про те, як чорні діри можуть дати життя молодим усесвітам, про теоретичну фізику і продовжує розпочаті ще Альбертом Ейнштейном спроби науковців визначити майбутнє Всесвіту.', 1, 170, 'Чорні_діри.jpeg', 10, 0),
(121, 'Астрофізика для тих, хто цінує час', 'Тайсон Н.', 'Яка природа простору і часу? Яке наше місце у Всесвіті? Яке місце Всесвіту в наших життях? Немає кращого провідника по цих усіх питаннях, аніж всесвітньовідомий астрофізик, популяризатор науки та автор бестселерів Ніл Деграсс Тайсон. Втім, мало хто з нас має час на споглядання зірок — тому Тайсон знайомить із ними чітко та лаконічно. Дотепний та цікавий текст поділено на невеликі порції, якими зручно підживлювати спраглий до відповідей мозок у перерві напруженого дня. Автор пропонує коротке, але осмислене ознайомлення з фун даментальними питаннями космосу: від Великого вибуху до чорних дір, від кварків до квантової механіки, від дослідження інших планет до пошуків життя у Всесвіті.', 1, 126, 'Астрофізика.jpeg', 10, 0),
(122, 'CorelDRAW X4.Шаг за шагом.', 'Ковтанюк Ю.', 'В ваших руках - уникальный курс освоения одной из популярнейших программы для создания компьютерной графики - CorelDRAW версии Х4. С помощью этой книги самый неподготовленный пользователь сможет достаточно быстро приступить к самостоятельной работе в CorelDRAW и созданию с помощью этой программы разных видов графики: для печати, презентаций и Интернета. Прочитайте книгу и убедитесь, что освоить искусство создания потрясающей графики в программе CorelDRAW совсем несложно. 2009 г.', 1, 96, 'CorelDraw.jpeg', 10, 0),
(123, 'Математика. Збірник задач з розв’язками для підготовки до ЗНО та ДПА', 'Ривкін А. А.', 'Збірник містить задачі, що пропонувалися на письмових і усних вступних іспитах з математики більш, ніж у 100 вищих навчальних закладах різних профілів. До всіх задач є відповіді, а до деяких — вказівки та розв’язки. Для учнів старших класів, абітурієнтів, слухачів підготовчих відділень та вчителів.\r\n', 1, 89, 'Математика.jpeg', 10, 0),
(124, 'Ілюстровані правила дорожнього руху України: навч. пос. (2020)', 'Дерех З. Д.', 'У посібнику наводяться ілюстровані пояснення положень Правил дорожнього руху, які були введені в дію з 1 січня 2002 р. постановою Кабінету Міністрів України № 1306 від 10 жовтня 2001 р. з урахуванням усіх змін (постанови Кабінету Міністрів України № 16 від 06.01.2005, № 538 від 30.06.2005, № 263 від 21.02.2007, № 879 від 27.06.2007, № 634 від 09.07.2008, № 876 від 01.10.2008, №№ 76, 124 від 11.02.2009, № 395 від 22.04.2009, № 934 від 03.09.2009, № 435 від 20.04.2011, № 620 від 09.06.2011, №  1029 від 26.09.2011, № 668 від 18.07.2012, № 111 від 11.02.2013, № 136 від 06.03.2013, № 162 від 04.06.2014, № 490 від 17.09.2014, № 869 від 21.10.2015, № 641 від 14.09.2016, № 790 від 09.11.2016 та № 161 від 22.03.2017 та № 660 від 30.08.2017, № 660 від 30.08.2017, № 883 від 10.11.2017, № 860 від 15.11.2017 та № 553 від 11.07.2018). Зміни в Правилах, прийняті постановами Кабінету Міністрів України № 883 від 10.11.2017, № 860 від 15.11.2017 та № 553 від 11.07.2018, виділені рожевим кольором. Продемонстровано понад 1200 реальних дорожніх ситуацій, а також дорожніх знаків і розмітки, що дозволяють легко засвоїти вимоги Правил. Ця книга також містить корисні поради та інформацію для водіїв-початківців і тих, хто керує транспортним засобом нетривалий час. Призначена для студентів і викладачів вищих навчальних закладів. Буде корисна для викладачів автонавчальних комбінатів, загальноосвітніх середніх шкіл, ліцеїв і гімназій, інженерно-технічних працівників автотранспортних підприємств, а також осіб, які проходять підготовку для одержання посвідчення водія на право керування транспортними засобами всіх категорій.', 1, 57, 'ПДР.jpeg', 10, 0),
(125, 'Курс колоїдної хімії (Поверхневі явища та дисперсні системи)', 'Дібрівний В. М.', 'У посібнику подано основні поняття і закони колоїдної хімії. Описано властивості та методи дослідження дисперсних систем та високомолекулярних сполук. Велику увагу приділено поверхневим явищам. Посібник розрахований на студентів хімічних спеціальностей вищих навчальних закладів.', 1, 50, 'Хімія.jpeg', 10, 0),
(126, 'Історія війн і військового мистецтва: в 3-х т. - Т.2 (Від професійних найманих армій до масових (мобілізаційних) армій (початок XVI - початок XXст.)', 'Войтович Л.', 'Військова справа охоплює всі сторони існування і розвитку будь-якого суспільства, а також стосунки між його членами. Виробнича культура, розвиток і прогрес технології, налагодження виробництва і поява нових технічних засобів завжди пов’язані з війною. Таким чином, можна стверджувати, що військова історія фактично є найбільш повною історією людського суспільства, яка охоплює всі сторони його буття. Другий том нашого видання присвячений історії війн і військового мистецтва від професійних найманих армій до масових (мобілізаційних) армій (початок XVI cт. — початок ХХ ст.). Основна увага приділена подіям і особистостям, які мали вплив на розвиток і еволюцію військової справи в Європі. Вперше розкрита участь і роль українців у війнах цього періоду. Праця розрахована як на спеціалістів, так і на широкий загал, всіх, хто цікавиться військовою історією.', 1, 912, 'Історія_війн.jpeg', 11, 0),
(127, 'Слідчо-наглядові справи Тараса Шевченка (1847-1859). Метаграфовані тексти', NULL, 'Це корпусне видання усіх збережених досі слідчих і наглядових справ Тараса Шевченка як цілісних діловодних комплексів поліцейсько-жандармського й військового відомств. Це перша повна академічна публікація шевченківських документів подібного типу. До неї увійшли метаграфовані тексти і факсимільне відтворення близько 400 документів за тринадцять років життя поета (1847-1859); із них 147 документів уперше вводяться до наукового обігу. Книга містить факсимільну публікацію нещодавно віднайдених понад тридцяти листів до Тараса Шевченка, що вважалися втраченими. Уперше публікуються документи XVIII ст., зібрані поетом під час служби в Тимчасовій комісії для розгляду давніх актів. Проведено реконструкцію масштабної картини півторастолітніх міграцій архівної шевченкіани. Для спеціалістів і широкого кола читачів.', 1, 683, 'Слідчо-наглядові_справи.jpeg', 11, 0),
(128, 'Каліграфія. Взаємовпливи шрифтів: теорія і практика; кирилиця і латиниця; історія і сучасність', 'Мітченко В. С.', 'Автор наукового видання поставив своїм завданням дослідити історичний шлях рукописної кирилиці в Україні від появи добуквених знакових систем на її теренах до сьогодення. Уперше аналіз української кирилиці проведено на тлі зміни мистецьких стилів у Європі. Для порівняльного аналізу історичного шляху кирилиці та латиниці стисло викладено історію латинських рукописних і складальних шрифтів. В альбомі подано близько 8оо репродукцій, авторських перемальовок і схем, які наочно демонструють основні етапи розвитку шрифтових форм та вплив рукописних шрифтів на складальні. Видання призначене для науковців, студентів і випускників художніх вузів, а також фахівців художньо-творчного спрямування, які працюють в галузі оформлення книги, графічного дизайну, реклами та створюють сучасний шрифтовий дизайн, узявши за основу кирилицю.', 1, 494, 'Каліграфія.jpeg', 11, 0),
(129, 'Український пантеон', 'Сегеда С.', 'Про життя, смерть і місця останнього спочинку визначних діячів української історії та культури від доби Київської Русі до поч. ХХ ст. у ілюстрованому науково-популярному виданні авторства відомого історика Сергія Сегеди\r\n', 1, 462, 'Пантеон.jpeg', 11, 0),
(130, 'Історія Запорізьких козаків', 'Яворницький Д.', 'Фундаментальна «Історія запорізьких козаків» у трьох томах (СПб, 1892,1895,1897) стала підсумком багаторічної дослідницької роботи вченого, розпочатої іще в студентські роки. Безпосередній намір написати цей твір виник у Яворницького 1889 p., після видання двотомної праці «Запоріжжя у залишках старовини й переказах народу». Для її створення Дмитро Іванович використав такі фундаментальні видання документів як «Архив Юго-Западной России», «Акты, относящиеся к истории Южной и Западной России», «Памятники, изданные временной комиссией», власні збірники, численні публікації окремих пам\'яток і документів. Велику увагу присвятив історик наративним джерелам, починаючи від Геродота й закінчуючи своїм сучасником, нащадком запорожців Коржем. Особливою пошаною автора користуються літописи й хроніки давньоруські, козацькі, польські. Загалом бібліографія першого тому (першого видання) налічує 170 позицій. Іншим, не менш суттєвим джерелом «Історії запорізьких козаків» були неопу-бліковані документи з російських та українських архівів, бібліотек, приватних зібрань. Широко залучив автор до свого твору й праці своїх попередників — Г. Міллера, С. Соловйова, М. Маркевича, О. Рігельмана, А. Скальковського, П. Куліша, М. Максимовича, М. Костомарова, В. Антоновича й десятків інших. Велику групу джерел, які використав Д. І. Яворницький, становили матеріальні пам\'ятки музейних і приватних колекцій, значний археологічний матеріал, здобутий під час численних експедицій. Величезного значення (особливо це помітно в першому томі) надавав автор фольклорно-етнографічним дослідженням регіону Запоріжжя, вивченню його топографії, топонімії, гідрографії, географії, ботаніки, зоології. Десятки разів обійшов він пішки всі землі Запорізьких вольностей, дослідив усі Січі, кілька разів ламав руки й ноги на порогах, ледь не загинув під час розкопок кургану — і все задля того, щоб відчути й збагнути героїчний дух історії, донести її до читача.', 1, 462, 'Козаки.jpeg', 11, 0),
(131, 'Біблія у гравюрах Гюстава Доре', NULL, 'Це видання книги Святого Письма містить 241 ілюстрацію французького художника Гюстава Доре. Виконані ще в 1864 — 1866 роках, вони досі залишаються взірцем живого осягнення засобами графіки багатовікової біблійної історії Старого і Нового Завітів від створення світу до Одкровення Йоана.', 1, 404, 'Біблія.jpeg', 11, 0),
(132, 'Злет і падіння Третього Райху. Історія нацистської Німеччини: у 2 т. Том перший', 'Шифер В.', 'Книжка «Злет і падіння Третього Райху» — це монументальне дослідження найнебезпечні-ших подій XX століття. Нині, коли після закінчення Другої світової війни минуло понад 70 років, нам здається неймовірною та загроза, яку несли у світ Гітлер і його імперія. Багаторічна робота з першоджерелами, особисті спостереження автора за підйомом нацистської Німеччини, 485 тонн документів від Міністерства закордонних справ Німеччини, безліч щоденників, записів телефонних розмов, ретельно збережених німцями, — усе це лягло в основу масштабної праці Вільяма Ширера. Дослідник зібрав максимально об\'єктивну інформацію про політичний злет Гітлера та його плани стосовно встановлення контролю над світом і з максимальною точністю довів свою розповідь до падіння Німеччини у вогненну прірву. Поєднання особистих спогадів та історичних фактів вирізняє цю книжку з-поміж решти праць, присвячених історії нацистської Німеччини.', 1, 383, 'Третій_рейх.jpeg', 11, 0),
(133, 'Філософія свободи', 'Попович М. В.', 'Книга «Філософія свободи» містить твори, маловідомі широкому читачеві, написані видатним українським філософом-гуманістом Мирославом Поповичем й ви-друковані у різні роки, у різних виданнях та країнах. Вона є вочевидь унікальною за розмаїттям тематики, часовим виміром і тим, що статті власноруч підбирав Мирослав Володимирович — в останні місяці свого життя. Твори, які включено до цього видання, дозволяють побачити об\'єкти досліджень автора під різними кутами зору, дають нам можливість побувати у майстерні філософа. І це стосується не лише історичних подій і портретів важливих для України персоналій, а й інтерпретації подій історії науки в їх раціонально-філософському відтворенні. Вчений прагне осмислити події, що відбуваються у нього на очах, порівнюючи їх зі світовим досвідом розвитку і шукаючи їхні корені в історичних джерелах українського народу, в особливостях української ментальності. При цьому він сповна використовує досвід розробки ло-гіко-методологічних проблем розвитку науки. Книга «Філософія свободи» — це пошук істини на перехресті національних і світових шляхів культури й науки, що нам передала у спадок мудра і світла людина — Мирослав Попович.', 1, 322, 'Філософія.jpeg', 11, 0);
INSERT INTO `books` (`id`, `name`, `author`, `description`, `status`, `price`, `image`, `categoryid`, `featured`) VALUES
(134, 'Колапс. Чому одні суспільства занепадають, а інші успішно розвиваються', 'Деймонд Дж.', 'Нас немов магнітом притягують монументальні руїни, що їх залишили по собі цивілізації минулого, вабить їхня видовищна краса і таємниці, які в них криються. Масштаб руїн свідчить про багатство й могутність тих, хто їх споруджував. Однак чому пішли у небуття не лише ті, хто будував ці споруди, а й власне суспільства, до яких вони належали? Куди зникли цілі народи? Якщо подалися геть, то куди і чому? Чи не загинули вони жахливою смертю? За цією романтичною таємницею не дає спокою бентежна думка: а чи не може схожа доля спіткати сучасне суспільство? Автор книги не лише пропонує новий погляд на історію, причини розквіту та занепаду давніх культур, а й окреслює виклики і загрози, які невпинно постають перед сучасною цивілізацією, описує можливі шляхи їх усунення.', 1, 315, 'Колапс.jpeg', 11, 0),
(135, 'Чорнобиль. Історія ядерної катастрофи', 'Плохій С.', 'Сергій Миколайович Плохій (нар. 1957) — український та американський історик, професор української історії в Гарвардському університеті, директор Гарвардського українського наукового інституту. Член Українського ПЕН-клубу. Ця книга є першою історією Чорнобильської катастрофи від вибуху 26 квітня 1986 року до закриття станції у грудні 2000-го. Чим для України був Чорнобиль? Національною трагедією, величезною психологічною травмою, важкою ношею для економіки. Чорнобиль вибухнув не тільки через помилки персоналу, нехтування правилами безпеки і проблемами з конструкцією реактора, але також через систему, яка обгородила ядерну енергетику завісою секретності. Радянська система не дозволяла поширювати інформацію про попередні аварії навіть серед фахівців. Це зробило нову масштабну аварію неминучою. Сьогодні реактори чорнобильського типу вже не будують, але авторитарні режими, які тримають повний контроль над інформацією, усе ще існують. Зрештою, і в Україні досі виробляють майже 50 % усієї електроенергії на атомних станціях. Нам є чим поділитися зі світом стосовно досвіду Чорнобиля, але є над чим задуматися і самим.', 1, 311, 'Чорнобиль.jpeg', 11, 0),
(136, 'Скульптура. Перетворення', 'Пінзель Іоанн Георг', 'Альбом присвячено спадщині одного з найзагадковіших майстрів барокової скульптури XVIII ст. Більша частина її зібрана у Львівському музеї сакральної скульптури, що був відкритий для глядачів у другій половині 90-х років минулого століття. Також до альбому входять зображення творів скульптора, що донині залишилися у вівтарях храмів м. Бучач (Тернопільська обл.) та у Тернопільському краєзнавчому музеї. Дизайн великоформатного альбому базується на концепції надблизького розглядання близько 50 скульптур, що стало можливим завдяки використанню сучасної оптики. Вступні статті героя України Б. Г. Возницького та відомого культуролога Тараса Возняка доповнені матеріалами, що стосуються епохи й значення спадку скульптора. Поряд із зображенням уміщено розлогі цитати з творів поетів та мислителів того часу.', 1, 297, 'Скульптура.jpeg', 11, 0),
(137, 'Механізм свободи. Анархія вільного ринку', 'Фрідман Д.', 'Професор права Девід Фрідман — теоретик анархо-капіталізму. Він переконаний: розвиток інститутів приватної власності є саме тим механізмом свободи, який дасть змогу кожній людині жити так, як їй хочеться. Чи може анархо-капіталізм бути кращим, ніж сучасна система? Чи реально створити суспільство на основі добровільної співпраці без керівних інституцій та ринкових взаємин? Як легалізація азартних ігор чи наркотиків може вплинути на суспільну динаміку? У цій книжці Фрідман, розглядаючи різні аспекти такого ідеологічного ладу, намагається переконати, що вільний ринок може не лише регулювати сферу товарів і послуг, а й диктувати закони та встановлювати правила, що діють у суспільстві.', 1, 296, 'Механізм.jpeg', 11, 0);
